css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google.), 
    url-prefix("https://www.google."), 
    url-prefix("http://news.google."), 
    url-prefix("https://mail.google.com/mail/")
{
/* Background */
  body, cnt.big
  {
    background: #fff url("https://i.imgur.com/hJIB0rc.png") fixed bottom left;
    background-repeat: no-repeat;
    background-position: right top;
    background-size: 30%;
    color: #145;
  }

    
/* Napis Google */
  #lga:after
  {
    display: block;
    content: 'Czego szukasz ?';
    font: normal 62px/220px 'Trebuchet MS',serif;
    color: #620000;
    text-shadow: 0 1px 0 #ccc, 0 2px 0 #c9c9c9, 0 3px 0 #bbb, 0 4px 0 #b9b9b9, 0 5px 0 #aaa, 0 6px 1px rgba(0,0,0,.1), 0 0 5px rgba(0,0,0,.1), 0 1px 3px rgba(0,0,0,.3), 0 3px 5px rgba(0,0,0,.2), 0 5px 10px rgba(0,0,0,.25), 0 10px 10px rgba(0,0,0,.2), 0 20px 20px rgba(100,100,100,.15);
    -webkit-transform: scaleY(.85);
    -moz-transform: scaleY(.85);
    transform: scaleY(.85);
    position: absolute;
    position: absolute;
    top: 100px;
    left: 10%;
  }

  div.sfbg, div.sfbgg {
    min-width: 0px;
    height: 0px;
}

#SIvCob {
    display: none;
}  
  
  #prm, #hplogo,
#cpBackgroundImg
  {
    display: none !important;
  }

  #gbx3, #gbx4
  {
    background-color: #4d6077 !important;
    border-bottom: 1px solid #3b495a !important;
  }

  .gbm
  {
    z-index: 9999 !important;
  }

  #gb div#gbx1
  {
    background: transparent !important;
    border-color: transparent !Important;
  }


/* Header */
  #appbar, #rshdr.rshdr, #appbar > div[style*="border-bottom"], #extabar > #topabar > div
  {
    background: transparent !important;
    border: none !important;
  }

/*Top Nav */

  #hdtb > div
  {
    background: #bcb8b9 !important;
    border-color: transparent !Important;
  }

  #top_nav > div
  {
    background: #bcb8b9 !important;
    border-color: transparent !Important;
  }


/* Left */
  #leftnav, #leftnav #hidden_modes, #leftnav #hmp
  {
    background: none !important;
  }

  #leftnav li.mitem a:hover, #leftnav .mitem.msel .kls:hover, #leftnav #ms #showmodes:hover
  {
    background: #cccccc !important;
  }

  #leftnav .lnsep
  {
    border-color: #5C5B5B !important;
  }

  div#leftnav
  {
    right: 0px !important;
  }

  #center_col
  {
    margin-left: 20px !important;
  }

  #fbar
  {
    line-height: 20px;
  }
/* Efekt wyszukiwania */ 
  .a, cite, cite a:link, cite a:visited, .cite, .cite:link, #mbEnd cite b, #tads cite b, #tadsb cite b, #ans > i, .bc a:link
  {
    color: #006633 !important;
    font-style: normal !important;
  }

/* Wynik wyszukania */  
  #resultStats, .ab_center_col
  {
    color: #333333 !important;
    font-size: 12px !important;
    line-height: 43px !important;
  }

  .hp #tsf
  {
    margin: 0 !important;
    width: 833px !important;
  }

  .hp #searchform
  {
    position: absolute;
    top: 290px;
  }

  .hp .big .tsf-p
  {
    padding-left: 100px !important;
    padding-right: 50% !important;
  }

  .hp .tsf-p
  {
    padding-left: 2px;
    padding-right: 80px;
  }

  #center_col
  {
    padding: 2px 80px!important;
  }

  .jhp > #gb
  {
    position: absolute!important;
    top: -280px!important;
    right: 10px!important;
    width: 100%!important;
  }

  [id="rhs_block"],
table[class="nrgt"]
  {
    display: none !important;
  }   
 
/*gmail informacje o działaniu*/
  .acts
  {
    background-color: rgb(0,102, 51)!important;
  }

  .fs
  {
    background-color: rgb(102,102,102) !important;
  }

  .thead
  {
    background: none repeat scroll 0% 0% rgb(255,255, 51)!important;
    font-size: 80% !important;
    color: rgb( 51, 51, 51)!important;
  }

  .med
  {
    font-size: medium;
    font-weight: normal;
    color: red;
    margin: 0px;
    padding: 0px;
  }

  div[class="_Ak c"],
    
/*gmail reklama z boku*/
    
td[class="Bu y3"],
div[class="AT"],
div[class="Zs"],
div[class="aKB"],
div[class="mq"]
  {
    display: none !important;}

}
