css:
@-moz-document domain("docs.craftcms.com"), domain("docs.api.craftcms.com") {
html,
.sidebar,
.navbar,
.navbar.links,
.nav-dropdown,
.links,
.ds-cursor,
.ds-cursor *,
ul.suggestions,
.search-box input {
    background: rgba(55, 71, 79, 1) !important;
}

p,
ul,
h1,
h2,
h3,
h4,
.sidebar,
.site-name,
.content code,
pre,
th,
dl,
#algolia-search-input,
[class*=algolia-],
[class*=ds-],
.search-box input{
    color: rgba(224, 224, 224, 1) !important;
}
a {
    color: rgba(79, 195, 247, 1) !important;
}

pre,
.custom-block,
div[class*=language-],
tr:nth-child(2n),
th,
dl,
.page:after,
.code-language-switcher,
#algolia-search-input,
[class*=algolia-],
[class*=ds-],
.ds-dropdown-menu:before,
ul.suggestions li:hover,
li.focused {
    background-color: rgba(38, 50, 56, 1) !important;
}

.sidebar {
    border-right: 1px solid rgba(38, 50, 56, 1);
}
.navbar {
    border-bottom: 1px solid rgba(38, 50, 56, 1);
}
.code-language-switcher {
    border-bottom: 2px solid rgba(38, 50, 56, 1);
}
.active {
    font-size: 1.1em;
}

.page-nav.content .inner {
    border-top: 1px solid rgba(38, 50, 56, 1);
}
#algolia-search-input {
    border: 1px solid rgba(38, 50, 56, 1) !important;
}
.algolia-docsearch-suggestion--highlight {
    color: rgba(79, 195, 247, 1) !important;
}
.algolia-autocomplete .ds-dropdown-menu .ds-suggestion.ds-cursor .algolia-docsearch-suggestion:not(.suggestion-layout-simple) .algolia-docsearch-suggestion--content,
.ds-cursor .algolia-docsearch-suggestion--content .algolia-docsearch-suggestion--highlight,
.ds-cursor .algolia-docsearch-suggestion--content .algolia-docsearch-suggestion--title,
.ds-cursor .algolia-docsearch-suggestion--content .algolia-docsearch-suggestion--text {
    background: rgba(55, 71, 79, 1) !important;
}
}
