}
     #items.ytd-grid-renderer,#container.ytd-masthead,ytd-page-manager > *.ytd-page-manager,ytd-guide-renderer[is-guide-refresh],#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child),paper-item.ytd-compact-link-renderer,#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer{

