}    
#container.ytd-sentiment-bar-renderer, #notification-count.ytd-notification-topbar-button-renderer,#like-bar,#toggleButton{
	background-color: rgba(	255	179	215) !important;
}
a.yt-simple-endpoint.yt-formatted-string:visited,.date.ytd-video-secondary-info-renderer,#title.ytd-metadata-row-renderer,ytd-button-renderer yt-formatted-string.ytd-button-renderer,#metadata-line.ytd-video-meta-block span.ytd-video-meta-block,#metadata-line.ytd-grid-video-renderer span.ytd-grid-video-renderer,yt-formatted-string[ellipsis-truncate] a.yt-formatted-string:last-child,yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string

