   fill: rgba(	255 255 255) !important;
}
.ytp-chrome-controls .ytp-button[aria-pressed]::after,#progress.ytd-thumbnail-overlay-resume-playback-renderer,.yt-uix-checkbox-on-off input-extras2-disabled[type="checkbox"]:checked + label,.video-extras-sparkbar-likes-extras2 {
