css:
@-moz-document domain(translate.google.com)
{
body {
    background-color: #333 !important;
}
header {
    background-color: #222 !important;
}
.header-button {
    background: #333 !important;
    border-color: #333 !important;
}
.gb_ic {
	background-color: #222;
}
.gb_wc, .gb_xc {
	color: rgba(255, 255, 255, 0.87);
}
.frame:before {
    background: #3c4043 !important;
}
.ls-wrap {
    background-color: #333;
}
.sl-sugg .goog-inline-block.jfk-button, .tl-sugg .goog-inline-block.jfk-button {
    color: #ccc;
}
.ls-wrap .sugg-fade {
    display: none;
}
.swap-wrap, .swap.jfk-button {
    background-color: #5f6368;
}
.source-wrap, .source-input, .orig {
    background-color: #3c4043;
    color: #f8f8f8;
}
.results-container, .result, .gt-edit, .contribute-target {
    background-color: #3c4043 !important;
}
.ft-icon-oval {
    background-color: #3c4043;
}
.ft-icon-txt {
    color: #8e8e8e;
}
.results-container:not(.empty) .placeholder, .result-dict-wrapper .translation, .contribute-target, #st-buttons .jfk-button {
    color: rgba(255, 255, 255, 0.87);
}
.gt-lc.gt-lc-mobile .gt-cc-r-i {
    background-color: #333!important;
}
.gt-lc.gt-lc-mobile .gt-cd {
    background-color: #3c4043;
    border-color: #3c4043;
}
.gt-cd-tl {
    color: #ccc;
}
.gt-card-ttl-txt {
    color: #fff;
}
.gt-def-row {
    color: #fff;
}
.gt-lc.gt-lc-mobile .gt-def-num {
    color: rgba(255, 255, 255, 0.38);
    border-color: rgba(255, 255, 255, 0.38);
}
.gt-is-mobile {
    background-color: #5f6368;
    border-color: #222;
}
.gt-is-sg, .gt-is-tr {
    color: #f1f1f1 !important;
}
.gt-def-example, .gt-def-synonym-title, .gt-ex-text {
    color: rgba(255, 255, 255, 0.54) !important;
}
.gt-ex-quote svg {
    fill: rgba(255, 255, 255, 0.54) !important;
}
.cd-expand-label {
    color: rgba(255, 255, 255, 0.87) !important;
}
.gt-lc.gt-lc-mobile .gt-cd-cl, .gt-lc.gt-lc-mobile .gt-cd-ncl {
    color: rgba(255, 255, 255, 0.54);
    background: #333;
    border-color: #333;
}
.gt-lc-mobile div.gt-baf-translations {
    color: rgba(255, 255, 255, 0.54);
}
.gt-baf-word-clickable {
    color: #fff;
}
.gt-lc-mobile .gt-baf-word-clickable:hover {
    background: #333;
}
.gt-cd-pos-pop {
    color: rgba(255, 255, 255, 0.54);
}
.gt-related-suggest-message, .gt-spell-correct-message {
    background-color: #45484a;
}
.gt-lc.gt-lc-mobile.show-as-one-card {
    background: #333;
    border-color: #333;
}
.gt-cc-t {
    background-color: #3c4043;
    border-bottom-right-radius: 0px !important;
    border-bottom-left-radius: 0px !important;
}
.gt-lc.gt-lc-mobile.show-as-one-card .gt-cc {
    border-color: #3c4043;
}
.gt-lc.show-as-one-card .gt-cc-r {
    border-color: #333;
}
.gt-cc-t .gt-ct-text {
    color: #fff;
}

#sl_list-search-box, #tl_list-search-box {
	background-color: #333;
}
.language-list-toolbar,
.language-list,
.language-list-search-box,
.language_list_sl_list,
.language_list_tl_list,
.language_list_section,
.language_list_item_wrapper {
	background-color: #333;
}
.language_list_item_wrapper:hover,
.language_list_item_wrapper:focus {
	background-color: #444;
}
.language_list_item_wrapper.item-selected {
	background-color: #444;
}
.language_list_item_language_name {
    color: rgba(255, 255, 255, 0.87);
}
.select-file-page {
	background: #333;
}
.select-file-section .choose-document-prompt {
	color: #fff;
}
.select-file-section .upload-filetypes-prompt {
	color: rgba(255, 255, 255, 0.54);
}
}
