css:
@-moz-document url-prefix("https://voice.google.com") {
/*--------------------------------------------------------------
>>>	TOPBAR
--------------------------------------------------------------*/

/* Top bar color */
 a[title="Google Voice"] > span {
   filter: invert(1) brightness(200%);
}
/* .gb_Wa .gb_Xd { Try using the style selector to avoid constant class name changes */
 header[role="banner"] > div:nth-child(2) {
   background: #10b2a2 !important;
   color: white;
}
/* hamburger menu */
 header[role="banner"] > div:nth-child(2) > div > div[aria-label="Main menu"] {
   filter: brightness(300%);
}
/* icons on the right */
 .mat-accent>span>mat-icon {
   filter: brightness(200) invert(1);
}
a[aria-label="Google apps"] {
   filter: brightness(200);
}
form[action="/search"] {
   filter: invert(1) brightness(200%);
}
/* top bar buttons */
header > div > div > div > div > * > gv-icon-button > base-icon-button > button {
    filter: invert(1);
}

/*--------------------------------------------------------------
>>>	Left Side Menu
--------------------------------------------------------------*/
/* brighter icons */
gv-side-nav > div > div > gmat-nav-list > a[aria-selected="false"] {
   filter: invert(1);
}
/* badge icon blue *//*
aka span.navItemBadge
*/
/* collapsed */
gv-side-nav > div > div > gmat-nav-list > a div > div > span {
   filter: invert(1);
}
/* expanded */
gv-side-nav > div.expanded > div > gmat-nav-list > a div > div > span {
   filter: invert(0);
}
/* background */
#gvPageRoot {
   background: black;
}

/*--------------------------------------------------------------
>>>	Conversation area
--------------------------------------------------------------*/
#messaging-view > div > md-content > gv-thread-details > div.layout-column {
   background: black;
   color: white !important;
}
/* buttons on the right */
gv-message-list-header > div > div > div[role="toolbar"] {
   filter: invert(1) contrast(400%);
}
md-content {
   color: white !important;
   background: black !important;
}
gv-icon-button > button.mat-icon-button {
   filter: invert(1)
}
/* type a name or phone number label */
mat-chip-list > div > md-input-container > label { 
   color: black !important;
}

/*--------------------------------------------------------------
>>>	Conversation List
--------------------------------------------------------------*/
/* default name */
gv-thread-item>div[aria-selected="false"]>div>div>gv-annotation {
   color: white !important;
}
/* default text */
gv-thread-item>div[aria-selected="false"] {
   color: #AAAAAA;
}
/* default text hover */
gv-thread-item>div[aria-selected="false"]:hover {
   color: #BBBBBB;
   background: #222222;
}

/* selected name */
gv-thread-item>div[aria-selected="true"]>div>div>gv-annotation {
   color: white !important;
}
/* selected text */
gv-thread-item>div[aria-selected="true"] {
   color: white;
	background: #555555;
}
/* selected text hover */
gv-thread-item>div[aria-selected="true"]:hover {
   color: white;
	background: #777777;
}

/*--------------------------------------------------------------
>>>	Suggested contact list
--------------------------------------------------------------*/
gv-contact-list {
   background: #333333 !important;
}
gv-contact-list> div > div > div > p {
   color: white !important;
}
/* Name */
gv-contact-list> div > div > div > gv-frequent-contact-card > div > div:nth-child(2) > div {
   color: white !important;
}
/* Phone Number */
gv-contact-list> div > div > div > gv-frequent-contact-card > div > div:nth-child(2) > div > span {
   color: #AAAAAA !important;
}
/* All Contacts */
/* Name */
gv-contact-item > div > div:nth-child(2) > gv-annotation {
   color: white !important;
}
/* Phone Number */
gv-contact-item > div > div:nth-child(2) > div {
   color: #AAAAAA !important;
}


/*--------------------------------------------------------------
>>>	Bottom Bar
--------------------------------------------------------------*/
/* Compose textarea */
md-content > gv-thread-details > div > div:nth-child(3) > gv-message-entry > div > div > md-input-container > textarea {
   color: white !important;
}
/* Add image button (on the bottom) */
md-content > gv-thread-details > div > div:nth-child(3) > gv-message-entry > div > div > gv-icon-button-ng2 > button.mat-icon-button {
   filter: invert(0) !important;
}
gv-message-entry > div > div > gv-icon-button-ng2 > button > span > mat-icon {
   color: white;
}
span > gv-icon-button-ng2 > button > span > mat-icon {
   color: black;
}
/* Attach image button */
gv-icon-button-ng2[icon-name="image"]>base-icon-button>button{
    filter: invert(1);
}
button.mat-icon-button[disabled][disabled] {
   color: grey !important;
}
/*--------------------------------------------------------------
>>>	Top Bar
--------------------------------------------------------------*/
/* Send new message button */
gv-messaging-view > div > div > md-content > div > div > div > div {
   color: #dbdad7 !important;
}
/* To button */
md-content > gv-thread-details > div > div > gv-recipient-picker > div > div > div {
   filter: invert(1);
}
/* add recipients button */
mat-chip-list > div > md-input-container {
   filter: invert(1);
}
a[title="Google Voice"] {
   background: unset !important;
}

/* search bar background */
header[role="banner"] > div:nth-child(2) > div:nth-child(2) {
	background-color: unset !important
}

/* buttons to the right of search bar */
header[role="banner"] > div:nth-child(2) > div:nth-child(2) > div:nth-child(3) > div {
	background-color: unset !important
}
gv-message-list-header > div > div > p:nth-child(1){
   color: white !important;
}
gv-message-list-header > div > div > p:nth-child(2) {
	color: grey !important;
}

/*--------------------------------------------------------------
>>>	Conversation Overflow menu
--------------------------------------------------------------*/
/* phone number for item in list */
md-menu-content {
   filter: invert(1) brightness(1.5);
}


/*--------------------------------------------------------------
>>>	Call Log
--------------------------------------------------------------*/
gv-call-log-thread-item > gv-thread-item > div > div > div > div > *:nth-child(1) {
   filter: invert(1);
}

/*--------------------------------------------------------------
>>>	Settings Page
--------------------------------------------------------------*/

/* top settings headings */
gv-account-number>div {
   color: white;
}
/* top captions */
gv-settings-section-block-subheader {
   color: gray;
}

/* calls captions */
gv-settings-section-block-content>div>div>div {
   color: gray !important;
}

/* lower section headings  */
gv-settings-section>div>h3 {
   color: white !important;
}

/* voicemail time */
gv-settings-block-header>div>div>div.creditSubheader {
   color: rgb(0, 121, 107);
}
/* sms time */
div[gv-test-id="sms-sender-time-stamp"]{
   color:lightgray;
}

/* Web notifications caption */
gv-settings-section>div>div>div>div>div {
   color: white !important;
}

/* headings */
gv-settings-block-header>div>div>div.header {
   color: white !important;
}
/* captions */
gv-settings-block-header>div>div>div.subheader:not(.creditSubheader) {
   color: gray !important;
}
/* mat-select */
mat-select>div>div {
   color: white !important;
}

/* fix Peoples pane and "Send new message" at the top of conversation list */
div.grey-900 {
   color: grey !important;
}

/* fix all the broken text colors to not override */ 
h3.grey-900 {
   color: unset !important;
}
h3.grey-900 {
   color: unset !important;
}
gv-account-number>div>h3.grey-900 gv-settings-section-block>div>div>div>div>div>div.grey-900 gv-messaging-settings>div>h3.grey-900 {
   color: unset;
}
gv-settings-section-block>div>div>div>div>div>div.grey-900 {
   color: unset !important;
}
gv-settings-section-block-content>div.grey-900 {
   color: unset !important;
}
gv-settings-section-block-content>div>div.grey-900 {
   color: unset !important;
}
gv-forwarding-device>div>div>div>div.grey-900 {
   color: unset !important;
}

gv-linked-device>div>div>div>div>div.grey-900 {
   color: unset !important;
}
gv-linked-phone>div>div.grey-900 {
   color: unset !important;
}
gv-voicemail-greeting>div>div>div>div>div.grey-900 {
   color: unset !important;
}
}
