css:
@-moz-document url-prefix("https://play.pocketcasts.com/users"), url-prefix("https://play.pocketcasts.com/web") {
body {
    background: #343a3e !important;
    background-color: #343a3e !important;
  }
  
  .app { background-color: #191817 !important; }
  .title { color: #6d6d6d !important; }
  .podcast-title { color: #6d6d6d !important; }
  .separator,td { border-color:#4a4a4a !important; }

  ::selection {
    background: rgba(199, 75, 68, .99) !important;
    color: #fff !important;
  }

  ::-moz-selection {
    background: rgba(199, 75, 68, .99) !important;
    color: #fff !important;
  }

  code {
    background: #343a3e !important;
    color: #c74b44 !important;
    border: 1px solid #4a4d50 !important;
  }

  a {
    outline: none !important;
  }

  /**
   * LOGIN
   */

  h1 {
    color: #92979e !important;
  }

  #sign_in_page p {
    color: #75797c !important;
  }

  #sign_in_page a {
    color: #92979e !important;
  }

  .form_text {
    background: #222628 !important;
  }

  .form-control:focus {
    background: #282c2e !important;
  }

  .primary_button {
    background: #c74b44 !important;
  }

  .primary_button:hover {
    background: #d95b53 !important;
  }

  .alert-danger {
    color: #75797c !important;
  }

  #error_explanation li {
    color: #75797c !important;
  }

  /**
   * HEADER
   */

  #header {
    background-color: #c74b44 !important;
  }

  #search_add_button:hover,
  .search_open #search_add_button,
  .dropdown.open,
  .dropdown:hover,
  .dropdown-menu {
    background: #343a3e !important;
  }

  .dropdown-menu a {
    color: #92979e !important;
  }

  .divider {
    background: #555c61 !important;
  }

  .dropdown-menu a:hover {
    background: #4c5256 !important;
  }

  .open .settings_cog,
  .dropdown:hover .settings_cog {
    opacity: .8 !important;
  }

  #search_add_button:hover .add_image,
  .search_open .add_image {
    opacity: .8 !important;
  }

  #search_input_value {
    background: #343a3e !important;
    color: #92979e !important;
  }

  #search_input_dropdown {
    background: #343a3e !important;
  }

  .angucomplete-row {
    border-top: 1px solid #4a4d50 !important;
  }

  .angucomplete-row:hover {
    background: #41474b !important;
  }

  .angucomplete-title {
    color: #92979e !important;
  }

  .angucomplete-description {
    color: #626669 !important;
  }

  /**
   * CONTENT
   */

  #content_middle,
  #discover_container {
    background-color: #343a3e !important;
  }

  #discover_container .section {
    background: #3e4448 !important;
    border: none !important;
  }

  .episode_row:hover,
  .episode_row.row_selected {
    background: #373d41 !important;
  }

  .podcast_title a {
    color: #626669 !important;
  }

  .episode_title a {
    color: #92979e !important;
  }

  .episode_button {
    opacity: .5 !important;
  }

  .episode_button:hover {
    opacity: .8 !important;
  }

  .episode_row_info {
    color: #585c61 !important;
  }

  .no_episodes_title {
    color: #92979e !important;
  }

  .no_episodes_summary {
    color: #626669 !important;
  }

  .episode_star {
    color: #585c61 !important;
  }

  .episode_more i {
    color: #585c61 !important;
  }

  .episode_popover {
    background: #222628 !important;
    box-shadow: none !important;
  }

  .episode_popover li:hover {
    background: #2c3134 !important;
  }

  .episode_star:hover {
    color: #cda02b !important;
  }

  #shownotes_page {
    background: #2c3134 !important;
  }

  .row_border {
    border-bottom: 1px solid #4a4d50 !important;
  }

  .mark_as_played {
    opacity: .6 !important;
  }

  h6 {
    color: #626669 !important;
  }

  h2 {
    color: #a3a9b0 !important;
    border-bottom: 1px solid #4a4d50 !important;
  }

  h3 {
    color: #a3a9b0 !important;
  }

  #content_right {
    border-left: 1px solid #4a4d50 !important;
    background: #2c3134 !important;
  }

  #content_left {
    background: #2c3134 !important;
    border-right: 1px solid #4a4d50 !important;
  }

  .episode_section {
    background-color: #2c3134 !important;
    border-bottom: 1px solid #4a4d50 !important;
  }

  .episode_section:hover,
  .episode_section.selected {
    background-color: #373d41 !important;
  }

  .podcast_search {
    background: #2c3134 !important;
    color: #92979e !important;
  }

  .podcast_search input {
    background-color: #343a3e !important;
  }

  .grid_button,
  .list_button {
    opacity: .4 !important;
  }

  .clear_search {
    opacity: .4 !important;
  }

  .author {
    color: #626669 !important;
  }

  .podcast_text {
    background: #2c3134 !important;
    border-bottom: 1px solid #4a4d50 !important;
  }

  .podcast_text .title {
    color: #92979e !important;
  }

  .podcast:hover .podcast_text {
    background: #373d41 !important;
  }

  .website a,
  .show_notes a {
    color: #4b8cd8 !important;
  }

  .episodes_sort {
    color: #626669 !important;
  }

  .episodes_sort_value {
    color: #92979e !important;
  }

  .episodes_date {
    color: #c43434 !important;
  }

  .episode_day {
    color: #626669 !important;
  }

  .episode_image {
    opacity: .9 !important;
  }

  .episode_panel .close_button {
    opacity: .3 !important;
  }

  .episode_panel .close_button:hover {
    opacity: .8 !important;
  }

  #content_container {
    background: #2c3134 !important;
  }

  .show_notes,
  .show_notes p,
  .show_notes dd {
    color: #70747a !important;
  }

  .subscribe a {
    opacity: .25 !important;
  }

  .subscribe a:hover {
    opacity: .8 !important;
  }

  .section_header {
    color: #92979e !important;
  }

  #discover_container .podcast_text {
    background: none !important;
    border: none !important;
  }

  #discover_container .author {
    color: #838892 !important;
  }

  .top_podcast:hover {
    background: #474d51 !important;
  }

  .podcast_row {
    border-top: 1px solid #565b5f !important;
  }

  .podcast_row:hover {
    background: #474d51 !important;
  }

  /**
   * PLAYER
   */

  .player_bottom {
    background: #222628 !important;
  }

  .player_top {
    background: rgba(98, 102, 105, 0.9) !important;
  }

  #seek_bar_player {
    background: #c43434 !important;
  }

  .seek_bar_played {
    background: #c43434 !important;
  }

  .player_controls div {
    opacity: .4 !important;
  }

  .player_controls div:hover {
    opacity: .8 !important;
  }

  .player_episode_date {
    color: #a3a9b0 !important;
  }

  .player_podcast_title {
    color: #a3a9b0 !important;
  }

  .player_episodes_date {
    color: #b5b9bf !important;
  }

  .seek_bar_knob {
    background: #585c61 !important;
  }

  .speed_value {
    opacity: .6 !important;
  }

  .mute_button {
    opacity: .7 !important;
  }

  /**
   * MODAL
   */

  .modal-content,
  .ngdialog-content,
  .dialog_panel {
    background: #222628 !important;
  }

  #settings_sidebar {
    border-right: 1px solid #4a4d50 !important;
  }

  #settings_sidebar a {
    color: #474747 !important;
  }

  #settings_panels p,
  #settings_panels li {
    color: #70747a !important;
  }

  #settings_panels h2 {
    border-bottom: none !important;
    border-top: 1px solid #4a4d50 !important;
  }

  #settings_panels .form-control {
    background: #282c2e !important;
    border: 1px solid transparent !important;
  }

  .sidebar_header {
    color: #8a9199 !important;
  }

  .modal-content .close {
    text-shadow: none !important;
    color: #fff;
  }

  .modal-header div {
    color: #a3a9b0 !important;
  }

  .modal-body {
    color: #70747a !important;
  }

  .btn-danger {
    background: #c74b44 !important;
  }

  .dialog_tab {
    color: #70747a !important;
  }

  .ngdialog-close {
    opacity: .3 !important;
  }

  .ngdialog-close:hover {
    opacity: .6 !important;
  }

  .share_header {
    background: #2c3134 !important;
    border: none !important;
  }

  .share_header_title {
    color: #8f97a4 !important;
  }

  .share_header_subtitle {
    color: #626669 !important;
  }

  .share_url {
    border: 2px solid #2c3134 !important;
    color: #8f97a4 !important;
  }

  .share_button {
    opacity: .6 !important;
  }

  .share_button:hover {
    opacity: .8 !important;
  }

}
