css:
@-moz-document domain("developer.apple.com"), domain("appstoreconnect.apple.com"), domain("help.apple.com"), domain("www.apple.com") {
html, body, .panels > .panel.fixed, nav.navigation-menu .menu-divider span {
    background: #000;
    color: white;
}
.w2ui-grid .w2ui-grid-body .w2ui-grid-records table tr{
    background: transparent !important;
}
.w2ui-grid .w2ui-grid-body {
    background: transparent !important;
}

.form-box {
    background: #000;
}
input[type='text'], input[type='email'], input[type='number'], input[type='password'], input[type='tel'], input[type='url'], textarea {
    color: white;
}

header.mobile, .bg-light, header.desktop a[href="#/"] {
    background: #222;
    color: white;
}

.hamburger-button {
    filter: invert(1)
}
.brandmark {
    filter: invert(1);
}
nav.navigation-menu li.menu-option a {
    filter: invert(1);
}

}

@-moz-document domain("appstoreconnect.apple.com"), domain("analytics.itunes.apple.com") {
:root {
    --dgdg : #101010
}
html, body {
    background: #000;
    color: whiite;
}
.pane-layout-sidebar,  .tf_wrapper:after {
    background: #000;
    color: #fff;
    border: none;
    border-right: #333 1px solid;
}
.pane-layout-sidebar li  {
    background: #000;
    color: #fff;
    font-weight: 700;
}
.pane-layout-sidebar li > a {
    color: #ddd;
}
.pane-layout-sidebar li.selected a, .tb-side-nav__menu .tf-single-line:hover {
    background: #222;
}
.pane-layout-content-header, .is-not-homepage .pane-layout-content-footer.footer, .itc-footer__links, .itc-footer {
    background: #000;

}
.pagemessage.info, .empty-state-block, input[type='text'], input[type='email'], input[type='number'], input[type='password'], input[type='tel'], input[type='url'], textarea, .customselect select, .customselect .selectCover, .tb-panel.tb-panel--empty, .tb-panel.tb-panel-empty, .tb-side-nav .tb-side-nav__menu li:hover a, .tb-side-nav .tb-side-nav__menu li:hover a:hover, .tidbits-tooltips .tb-content {
    color: white;
    border: none;
    background: var(--dgdg) !important;
}

#itc-globalnav {
    background: #121212;
}
#appPageHeader, .itc-header.tb-default-theme, .appslist .header, .appslistheader, .search-input, .controlBar {
    background: #121211;
    color: white;
    border:none;
}
#appMainNav li a {
    color: white;
    border: none;
}
.pane-layout-sidebar-newSection {
    border-top-color: var(--dgdg);
}
.itc-menuicon-label, .itc-logo-alt, .itc-menu__selected-icon__display__text, .tb-dropdown__icon.tb-icon--down {
    filter: invert(1)
}

.tb-popover__clickarea .tb-dropdown__icon.tb-icon--down{
    filter: none;
}

.section, .pane-layout-content-header, .tb-primary-nav, #appPageHeader .app-header-nav-links:after, .pane-layout-content-header.has-subnav .pane-header-subnav{
    border-bottom: 1px solid var(--dgdg);
}

.is-not-homepage .footer {
    border-top-color: var(--dgdg);
    background: #000;
}
.tb-table th, .tb-default-theme th, .tb-table table th, .tb-default-theme table th, .defaultTable th, .openTopTable th, .defaultTable .th, .openTopTable .th, .defaultTable th, .openTopTable th, .defaultTable .th, .openTopTable .th, .defaultTable td, .openTopTable td, .tablearea .dualtable thead {
    background: transparent !important;
    border-color: var(--dgdg);
    border-bottom-color: var(--dgdg);
    color: white;
}
.tf-header, .tf-toggle-header, .tb-type h2, .tb-default-theme h2, .tb-form label, .tb-default-theme label, .tb-form .tb-form__label, .tb-default-theme .tb-form__label, .tb-form .tb-form__group__label, .tb-default-theme .tb-form__group__label{
    color: white;
}
.tf-question-tip .tb-info {
    filter: invert(1)
}
.about_area .pane-layout-sidebar li.selected a, .tb-side-nav__menu .tf-single-line:hover {
    background: initial;
}

.tb-tooltip-tip:after, .is-not-homepage .footer ul li {
    border-right-color: var(--dgdg) !important;
}
.tidbits-tooltips.tb-fromright .tb-tooltip-tip:before {
    border-right: none;
}
.tb-modal__box, .tb-modal__box--bare {
    color: white;
    background: #0a0a0a;
}
.tb-btn, .tb-type button.tb-btn, .tb-default-theme button.tb-btn, .tb-form button.tb-btn, .tb-default-theme button.tb-btn, .tb-type a.tb-btn, .tb-default-theme a.tb-btn, .tb-form a.tb-btn, .tb-default-theme a.tb-btn, .tf-table th, .tb-table th, .tb-default-theme th, .tb-table table th, .tb-default-theme table th, #reviews-header {
    color: white;
    background: var(--dgdg);
    border:none;
}

.tb-modal__footer .tb-btn--disabled {
display: none !important;
}
.tb-modal__footer {
    border:none;
}
.tb-form .tb-form__input, .tb-default-theme .tb-form__input, .tb-form input[type=date], .tb-default-theme input[type=date], .tb-form input[type=datetime], .tb-default-theme input[type=datetime], .tb-form input[type=datetime-local], .tb-default-theme input[type=datetime-local], .tb-form input[type=email], .tb-default-theme input[type=email], .tb-form input[type=month], .tb-default-theme input[type=month], .tb-form input[type=number], .tb-default-theme input[type=number], .tb-form input[type=password], .tb-default-theme input[type=password], .tb-form input[type=range], .tb-default-theme input[type=range], .tb-form input[type=search], .tb-default-theme input[type=search], .tb-form input[type=tel], .tb-default-theme input[type=tel], .tb-form input[type=text], .tb-default-theme input[type=text], .tb-form input[type=time], .tb-default-theme input[type=time], .tb-form input[type=url], .tb-default-theme input[type=url], .tb-form input[type=week], .tb-default-theme input[type=week], .tb-form textarea, .tb-default-theme textarea, .datepicker .innerinfo{
    color: white;
    border: none;
    background: var(--dgdg) !important;
}
.tb-loading__bg, .tb-spinner__bg, .loading, .contentloading, .pane-loading, .preloader{
    background-color: #000;
}
.tf-table th {
    background-color: #000 !important;
}
.pane-layout-content-header.has-subnav .pane-header-subnav li.current a {
    color: white;
}
table {
    border-radius: 6px;
    border:none;
    background: var(--dgdg);
}

.tb-table th, .tb-default-theme th, .tb-table td, .tb-default-theme td, .tb-table table th, .tb-default-theme table th, .tb-table table td, .tb-default-theme table td {
    border-bottom: none ; 
}
.homepageWrapper .main-nav-item img {
        filter: hue-rotate(-180deg) invert(1);
}

.tb-popover__content {
    filter: invert(1) hue-rotate(180deg);
    box-shadow:0 0 15px rgba(255, 254, 254, .149), 0 0 1px rgba(255, 255, 255, .047)
}
}

@-moz-document domain("analytics.itunes.apple.com") {
table {
    margin-top: 10px;
}
}
