css:
@-moz-document domain("katolo.gy") {
body, body, .normal-font {
    line-height: 26px;
    font-size: 15px;
    font-family: "Ubuntu", sans-serif!important;
}
body .site-content .stmt-posts-slider.stmt-posts-slider-style_1 .stmt-ps-navigation .container .stmt-ps-nav-item .stmt-ps-nav-item-meta:before, body .site-content .stmt-posts-slider.stmt-posts-slider-style_2 .stmt-ps-navigation .container .stmt-ps-nav-item .stmt-ps-nav-item-meta:before {
    content: '';
    display: block;
    width: 99.8%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    background: #222222;
    opacity: 1;
    z-index: 1;
    -webkit-transition: all .3s ease-in-out;
    transition: all .3s ease-in-out;
}
body .site-content .stmt-posts-slider.stmt-posts-slider-style_1 .stmt-ps-navigation .container .stmt-ps-nav-item .stmt-ps-nav-item-meta h4, body .site-content .stmt-posts-slider.stmt-posts-slider-style_2 .stmt-ps-navigation .container .stmt-ps-nav-item .stmt-ps-nav-item-meta h4 {
    color: #FFFFFF;
}
body .site-content .stm-grid-view-block.style_1 .container > div > div .grid_view_item .meta-bottom .meta li a {
    font-size: 11px;
    border-radius: 20px;
    border-top-left-radius: 9px;
    border-top-right-radius: 20px;
    border-bottom-right-radius: 20px;
    border-bottom-left-radius: 20px;
    text-transform: uppercase;
    font-weight: 300;
    padding: 1px 10px;
    letter-spacing: 1px;
    border-top-left-radius: 9px;
}
img {
    border-radius: 12px;
}
body .site-content .stmt-posts-slider.stmt-posts-slider-style_1 .stmt-ps-content .stmt-ps-wrap .container .stmt-ps-meta .stmt-ps-category ul li {
    display: inline-block;
    line-height: 20px;
    margin-bottom: 3px;
    border-radius: 5px;
}
.stmt-template-demo_three.single-post #wrapper .site-content .stmt-single-style_4 h1 {
    font-size: 40px;
    font-weight: 700;
    line-height: 52px;
    margin-bottom: 16px;
    color: rgba(79,190,110,1);
}
.single-post #wrapper .site-content .stmt-single-style_4 .header-4-wrap .post-meta-wrap, .single-attachment #wrapper .site-content .stmt-single-style_4 .header-4-wrap .post-meta-wrap {
    margin-bottom: 26px;
    color: #4fbe6e;
}
.single-post #wrapper .site-content .stmt-single-style_1 .post-meta-wrap .post-author .normal-font, .single-post #wrapper .site-content .stmt-single-style_2 .post-meta-wrap .post-author .normal-font, .single-post #wrapper .site-content .stmt-single-style_3 .post-meta-wrap .post-author .normal-font, .single-post #wrapper .site-content .stmt-single-style_4 .post-meta-wrap .post-author .normal-font, .single-post #wrapper .site-content .stmt-single-style_5 .post-meta-wrap .post-author .normal-font, .single-attachment #wrapper .site-content .stmt-single-style_1 .post-meta-wrap .post-author .normal-font, .single-attachment #wrapper .site-content .stmt-single-style_2 .post-meta-wrap .post-author .normal-font, .single-attachment #wrapper .site-content .stmt-single-style_3 .post-meta-wrap .post-author .normal-font, .single-attachment #wrapper .site-content .stmt-single-style_4 .post-meta-wrap .post-author .normal-font, .single-attachment #wrapper .site-content .stmt-single-style_5 .post-meta-wrap .post-author .normal-font {
    font-weight: 600;
    color: rgba(79,190,110,1);
}
input[type=submit] {
    border-style: none;
    background-color: #4fbe6e;
    color: #ffffff;
    border-radius: 3px;
    overflow: visible;
    transition:0.3s
}
input[type=submit]:hover {
    border-style: none;
    background-color: #222222;
    color: #ffffff;
    border-radius: 3px;
    overflow: visible;
    font-size:20px;
}
.obfx-sharing-inline a.btn-mail, .obfx-sharing a.mail {
    background-color: #5d3a96;
}
}
