@-moz-document url-prefix('https://www.messenger.com/') {

a {
  height: 60px;
  width: 100%;
  background: rgb(246,252,209);
  opacity: 0.2;
  transition: opacity 4s ease 0.4s, transform 0.8s ease;
  transform: scale(1);
}



a:hover {
  opacity: 1;
  transition: opacity 0.2s ease;
  transform: scale(1.04);
}


._4_j5 {background: lightblue;}

.uiScrollableArea {background: rgb(246,252,209, 0.5);}

._30yy img, ._4rv9{
    filter: none !important;
}


/* Changing the color of all icons */
._30yy img {
	background-color: rgb(246,252,209, 0); 
}

._2v6o {
	background: rgb(255, 140, 105, 0.75);
	color: black;
  -webkit-box-sizing: content-box;
  -moz-box-sizing: content-box;
  box-sizing: content-box;
  width: 150px;
  height: 15px;
  border: none;
  text-align: center;
  -o-text-overflow: ellipsis;
  text-overflow: ellipsis;
  -webkit-box-shadow: 10px 10px 28px 10px rgba(255, 140, 105, 0.91);
  box-shadow: 3px 3px 18px 3px rgba(255, 140, 105, 0.91) ;

}



