css:
@-moz-document domain("mongodb.github.io") {
  code {
      font-size: 16px!important;
      background: rgb(69, 76, 89);
      box-shadow: rgba(0, 0, 0, 0.2) 0px 3px 1px -2px, rgba(0, 0, 0, 0.14) 0px 2px 2px 0px, rgba(0, 0, 0, 0.12) 0px 1px 5px 0px;
      color: #ddd;
  }

  a {
      text-decoration: underline;
  }

  .prettyprint {
      background: rgb(51, 56, 66);
      border: none;
      box-shadow: rgba(0, 0, 0, 0.2) 0px 3px 1px -2px, rgba(0, 0, 0, 0.14) 0px 2px 2px 0px, rgba(0, 0, 0, 0.12) 0px 1px 5px 0px;
      border-radius: 0;
  }

  body {
      background: rgb(40, 44, 52);
      color: #bbbbbb;
  }

  .pln {
      color: #e06c75
  }

  .com {
      color: #bbbbbb;
  }

  .kwd {
      color: #c678dd;
  }

  .pun {
      color: #abb2bf;
  }

  .typ {
      color: #61afef;
  }

  .str {
      color: #98c379;
  }

  .lit {
      color: #d19a66;
  }

  .navigation {
      background: rgb(33, 37, 43);
  }

  .main .description p {
      font-size: 15px;
  }

  ::-webkit-scrollbar-thumb {
      background: rgba(78, 86, 102, 0.5);
      border-radius: 0;
  }

  ::-webkit-scrollbar {
      width: 14px;
      background: inherit;
  }

  .navigation li.item .title {
      font-size: 14px;
  }

  .input-sm {
      font-size: 14px;
      border-radius: 0;
  }

  .navigation .search input {
      background: rgb(27, 29, 35);
      border: none;
      box-shadow: rgba(0, 0, 0, 0.2) 0px 3px 1px -2px, rgba(0, 0, 0, 0.14) 0px 2px 2px 0px, rgba(0, 0, 0, 0.12) 0px 1px 5px 0px;
  }

  .form-control:focus {
      box-shadow: none;
      outline: 1px solid rgb(82, 139, 255);
  }

  .navigation li.item ul > li, .main table .optional {
      font-size: 12px;
  }

  .main table .type , .main dd {
      font-size: 14px;
  }

  .main table .optional {
      color: #98c379;
      background: rgb(69, 76, 89)!important;
  }

  .tag-deprecated {
      background: #e06c75!important;
  }
}
