      { color: #DE3163 !important; background-color: #323232 !important; }
[id*="icon search"], [class*="icon search"], [id*="icon-search"], [class*="icon-search"]  
      { color: #FF00FF !important; background-color: #FF00FF !important; }   /* 搜尋按鍵測試中 */


select, a.TabLink, .install[onclick] 
      { -moz-appearance: none !important;
        -webkit-appearance: none !important;
        transition: border-color 0.3s !important;
        background: none !important;
        background-color: #FF00FF !important;    /* 測試底色 原色：#f103f4 */
        color: #BBB !important;
        text-shadow: 0 1px #000 !important;
        border: 2px solid #333 !important;
        border-radius: 4px !important;
        box-shadow: 0 0 2px rgba(0,0,0,.9) !important; }
a[href="javascript:;"], a[class*="button"]:not(:empty), a[id*="button"]:not(:empty), a[id*="Button"]:not(:empty), div[class*="button"][onclick] 
      { transition: border-color 0.3s !important;
        background-color: #323232 !important; 
        color: #BBB !important;
        text-shadow: 0 1px #000 !important;
        border-color: #333 !important;
        box-shadow: 0 0 2px rgba(0,0,0,.9) !important; }
a[href="javascript:;"]:hover, a[class*="button"]:not(:empty):hover, a[id*="button"]:hover, a[id*="Button"]:not(:empty):hover, div[class*="button"][onclick]:hover { background-color: #151515 !important; color: #FFF !important; }
input *, select *, button *, a.button *, a.submit * { color: #BBB !important; text-shadow: none !important; }
input:not([id*="search"]):not([class*="search"]):not([name*="search"]):not([id*="query"]):not([class*="query"]):hover, input[type="button"]:hover, select:hover, button:hover, a.button:hover, a.submit:hover, a.BigButton:hover, a.TabLink:hover /*----- [role="button"]:hover已去除 -----*/ 
      { border: 2px solid #555 !important;
        border-top-color: #555 !important;
        border-bottom-color: #555 !important;
        border-left-color: #555 !important;
        border-right-color: #555 !important; }
input:focus, select:focus { box-shadow: 0 0 5px #077 !important; }
input *:hover * { color: #F0F0F0 !important; text-shadow: 0 0 2px #FFF !important; }
input[disabled], select[disabled], button[disabled], input[disabled]:hover, select[disabled]:hover, button[disabled]:hover, input[disabled]:focus, select[disabled]:focus, button[disabled]:focus 
      { opacity: 0.5 !important; border-color: #333 !important; }

input[type="checkbox"] { border-radius: 1px !important; }
input[type="radio"], input[type="radio"]:focus { border-radius: 100% !important; }
input[type="checkbox"], input[type="radio"] {min-width: 12px; min-height: 12px}
input[type="checkbox"]:checked, input[type="radio"]:checked 
      { border-color: #077 !important; box-shadow: 0 0 5px #077 !important; }

