@-moz-document url-prefix("https://mouniassn.pixnet.net/") {
/* 牟尼精舍 :: 痞客邦 :: */

body,.article, .hslice, .box {background: transparent !important}
*:before, *:after {background-color: transparent !important; border-color: #444 !important}

/*#####--- 縮減文章方框左右的白色邊框 ---#####*/
DIV[class="article"] { margin-right: -20px !important; margin-left: -20px !important; } 
UL[class="article-head"] { margin-right: 20px !important; margin-left: 20px !important; }


* { font-family: "微軟正黑體" !important; line-height: 1.42em !important; }


h3[class="article-area-title"] { font-size: 15px !important; background-image: linear-gradient(rgba(65, 13, 155, .7) 0px, rgba(128, 0, 230, .7) 100%) !important; background-repeat: repeat-x !important;}


div[id="view-mode"] { font-size: 15px !important; background: linear-gradient(to left, rgb(58, 212, 255) 0%, rgb(5, 148, 188) 60%, rgb(3, 84, 107) 100%) !important;} 


h4[class="box-title entry-title"] { color: #7633FF !important; font-size: 19px !important; line-height: 1.5em !important; }


div[class="box-more"] { margin-top: 145px !important; }

/*#####--- 下方留言區文字 ---#####*/
LI[class="post-text"] { color: #e7d2fb !important; font-size: 19px !important; line-height: 1.5em !important; } 

/*#####--- 下方留言區回復文字 - 霧玫瑰色 ---#####*/ 
LI[class="reply-text"] { color: #FFE4E1 !important; font-size: 19px !important; } 

/*#####--- 下方送出留言區 #fffacd-檸檬綢色 ---#####*/
TEXTAREA[id="comment_text"][name="blogcommenttext_body"] { color: #fffacd !important; font-size: 19px !important; line-height: 1.42em !important; padding-left: 5px !important; background: none !important; background-color: #1E1A1A !important; border: 2px solid #FF00FF !important; height: 500px !important; }

/*#####--- 清除留言區、最上方登錄雜物、推薦各類文章之廣告方框 ---#####*/
DIV[class="choose-identity"], DIV[class="tag-container-parent"], DIV[class*="pixnavbar__top-bar"], div[id*="dablewidget_"] {display: none !important;}
/*#####--- 隱藏 "痞客邦" 右側邊欄 下方留言區跳出登入方框 ---#####*/
DIV[id="pilot"], DIV[class="pixgame"] { display: none !important; }

/*#####--- 調整最上方圖片方框距離 ---#####*/
DIV[id="banner"] { font-size: 9px !important; margin-top: -70px !important; }
DIV[id="header"] { padding-top: 75px !important; }
UL[id="navigation"] { margin-top: 95px !important; }



}
