@-moz-document domain('www.omegle.com') {
    body {
        background: rgb(59, 59, 59);
    }
    #header, #intro {
        background: rgb(80, 80, 80);
        box-shadow: none;
        border-bottom: solid rgb(68, 66, 64) 1px;
    }
    #header #tagline img {
        visibility: hidden;
    }
    img[src*="textbtn.png"], img[src*="textbtn@2x.png"] {
        height: 0 !important;
        width: 0 !important;
        padding-left: 124px !important;
        padding-top: 50px !important;
        background: url(http://imgur.com/lKeQDhJ.png) no-repeat !important;
        background-size: 100% !important;
    }
  div img[src="/static/usausausa.jpeg"]
  {
    display: none;
  }

  div#introtext a img
  {
    display: none;
  }
  
   img[src*="exclamationog.png"], img[src*="exclamationog@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 25px !important;
    padding-top: 25px !important;
    background: url(http://i.imgur.com/3pnbg5e.png) no-repeat !important;
    background-size: 100% !important;
  }

  img[src*="textbtn.png"], img[src*="textbtn@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/7FV5emK.png) no-repeat !important;
    background-size: 100% !important;
  }

  img[src*="videobtn-enabled.png"], img[src*="videobtn-enabled@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/IObxrIp.png) no-repeat !important;
    background-size: 100% !important;
  }

  img[src*="videobtn-disabled.png"], img[src*="videobtn-disabled@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/2KY9hPB.png) no-repeat !important;
    background-size: 100% !important;
  }

  img[src*="newchatbtn.png"], img[src*="newchatbtn@2x.png"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 170px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/jFTIxxw.png) no-repeat !important;
    background-size: 100% !important;
  }

  #tagline img
  {
    display: none !important;
  }
    a#spymodebtn[style], a#videobtnunmoderated[style] {
        width: 124px !important;
    }
    .goog-te-gadget-simple {
        visibility: hidden !important;
    }
    .topictag {
        background: rgb(39, 39, 39);
    }
    .topictageditor, .topicplaceholder, .topicplaceholder:focus {
        background: rgb(59, 59, 59) !important;
        border: none;
        color: rgb(20, 20, 20);
    }
    #mobilesitenote, #topterms, .chattypeorcell, .logitem .statuslog, .newchatbtnwrapper a, .logitem a, #feedback div a {
        color: rgb(20, 20, 20);
        text-decoration: none;
    }
    .logwrapper, .disconnectbtnwrapper, .sendbtnwrapper, .sendbtn, .chatmsgwrapper, .chatmsg {
        background: rgb(80, 80, 80) !important;
        border-radius: 1px !important;
        border: rgb(59, 59, 59) solid 1px!important;
        box-shadow: none;
    }
    .logwrapper {
        margin: 0;
        width: 98.5%;
    }
    .chatmsgcell {
        margin: 0;
        width: 100%;
    }
    .chatmsgwrapper {
        border: none;
    }
    #intro {
        position: relative;
        left: 0;
        right: 0;
        margin-left: auto;
        border: solid rgb(68, 66, 64) 1px;
    }
    #monitoringnotice, #monitoringnotice h2, #monitoringnotice #adultalternative, #monitoringnotice #unmonitoredvideobtn {
        border-radius: 1px;
        box-shadow: none;
        background: rgb(59, 59, 59);
        color: white;
    }
    #monitoringnotice {
        border: rgb(39, 39, 39);
    }
    #feedback {
        position: relative;
        width: 114%;
        text-align: center;
    }
    #feedback p, #onlinecount, #onlinecount strong {
        color: rgb(39, 39, 39);
    }
    tbody img, tbody a {
        display: none;
    }
    .youmsg .msgsource, .youmsg span {
        color: rgb(201, 201, 201);
    }
    .strangermsg .msgsource, .strangermsg span {
        color: rgb(241, 241, 241);
    }
    ::-webkit-scrollbar {
        background: rgb(59, 59, 59);
        width: 9px;
    }
    ::-webkit-scrollbar-thumb {
        background: rgb(80, 80, 80);
    }
    ::-webkit-scrollbar-thumb:hover {
        background: rgb(70, 70, 70);
    }
    #videologo {
        visibility: hidden !important;
    }
    #videowrapper {
    }
    #othervideo {
        position: absolute;
        left: 0;
        top: 0;
        width: 300% !important;
        height: 90% !important;
        top: -250px !important;
    }
    #selfvideo, #othervideo {
        background: transparent;
    }
    #selfvideo {
        position: absolute;
        left: 230%;
        top: 0;
        width: 400px !important;
        height: 90% !important;
        top: -250px !important;
        left: 800px !important;
    }
    .logwrapper {
        top: 60% !important;
        margin-left: 2% !important;
    }
    .controlwrapper {
        margin-left: 2% !important;
    }
  #onlinecount:before
  {
    font-size: 10px;
    color: #555;
    content: "Omegle Incognito redesign by Unknown Coding";
  }

}
