css:
@-moz-document domain("zerohedge.com") {
body {
    background: #262524 !important;
    font-size:medium !important;
}
h1 {
    color: #8ba446 !important;
}
#header, .menu--zero-hedge-reads h2 {
    background: #3e3e3e !important;
    color: #b9a993 !important;
}
.node--sticky {
    background: rgba(100, 100, 100, 0.3) !important;
}
.node--type-contributor-article {
    background: rgba(30, 30, 30, 0) !important;
}
.view-content {
    background: #333 !important;
}
.node {
    background: #333 !important;
    color: #b9a993 !important;
}
.node span,
.field--name-comment-body p,
.up-current-score,
.down-current-score  {
    color: #8ba446 !important;
}
.sidebar-left-wrapper .block,
.sidebar-right-wrapper .block-wrapper {
    background: #333 !important;
    color: #b9a993 !important;
}
a, .dropdown__title {
    color: #559c7a !important
}
.view-articles, .views-row, .placement-view-native, .placement-native {
    border-color: #000 !important;
}
#header .header-left .menu--header-menu > ul li {
    border-color: #000 !important;
}
.node--type-article.node--view-mode-full .node__meta, .node--type-contributor-article.node--view-mode-full .node__meta, .node--type-page.node--view-mode-full .node__meta {
    border-color: #000 !important;
}
.node--type-article.node--view-mode-full .extras-section, .node--type-contributor-article.node--view-mode-full .extras-section, .node--type-page.node--view-mode-full .extras-section {
    border-color: #000 !important;
}
.dropdown {
	border-left: 10px solid #559c7a;
}
.talk-load-all-comments, .talk-stream-show-all-comments-button-container, .talk-stream-show-all-comments-button, .talk-load-more button {
	border: 1px solid #333;
	color: #559c7a;
    box-shadow: 3px 3px 2px black;
    background-color: #262524;
}
.talk-load-all-comments:hover, .talk-load-all-comments:active, .talk-load-more button:hover {
	background-color: #333;
	border-color: #333;
    border: 1px solid #333;
	color: #559c7a;
    box-shadow: 5px 5px 2px black;
}
footer[role="pagefooter"] {
    display:none;
}
.block-views-blockcontributors-block-1 {
	margin: 0;
}
input[type="submit"] {
	background: #559c7a;
	transition: background 200ms;
	color: #000;
}
.node.node--view-mode-full img, figure.caption ,figcaption, .max-width-video-wrapper {
	height: auto !important;
	max-width: 1000px !important;
    width:100% !important;
}
.node.node--view-mode-full figure.caption figcaption {
	background: #2c2c2c;
	color: #666;
}

.node.node--view-mode-full iframe {
	max-width: 100%;
    height:100%;
}
.cke_editable *, .talk-embed-stream * {
	color: #b9a993;
	font-size: large;
}
.CommentContent__content___1QcVR blockquote {
	background-color: #393939;
}
.node__content {
	padding-left: 20px;
	padding-right: 20px;
}
}
