css:
@-moz-document domain("twitter.com") {
@font-face
{
    font-family: rosetta;
    src: url('https://abs.twimg.com/a/1462524371/font/rosetta-icons-Regular.woff');
    font-weight: 100;
    font-style: normal;
}
.Icon::after,
.Icon::before
{
    display: block;
    font-family: rosetta;
}
.Icon--caretDownLight::before
{
    content: "\f196";
    font-family: "edgeicons" !important;
}
.Icon--dotsVertical::before
{
    content: "\f149";
    font-family: "edgeicons" !important;
}
.Icon--homeFilled::before
{
    content: "\00F053";
}
.Icon--notificationsFilled::before
{
    content: "\f055";
}
#global-new-tweet-button::before {
    content: "\f029";
    font-family: rosetta;
    font-size: 24px;
    position: relative;
    font-weight: 100;
    top: 6px;
}
button#global-new-tweet-button {
    padding: 0px 16.844px 7px;
}

EdgeButton, .EdgeButton:visited {
    border-radius: 5px;
}
.nav .session .dropdown-toggle {
    border-radius: 5px;
}
.EdgeButton, .EdgeButton:visited {
    border-radius: 5px;
}
.ProfileAvatar-image, .ProfileAvatar-placeholderImage {
    border-radius: 5px;
}
.ProfileAvatar {
    border-radius: 5px;
}
.avatar {
    border-radius: 5px;
}
.ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo {
    color: #aab8c2;
}
.ProfileTweet-action--favorite:hover .HeartAnimation, .ProfileTweet-action--favorite .ProfileTweet-actionButton:focus .HeartAnimation {
    background-position: 92.871429%;
}
.DashboardProfileCard-avatarImage {
    border-radius: 5px;
}
.top-timeline-tweetbox .tweet-user {
    border-radius: 6px 6px 0px 0px;
}
.stream-end-inner {
    border-radius: 0px 0px 6px 6px;
}
.avatar--circular {
    border-radius: 5px;
}
.nav .session .dropdown-toggle:before {
    border-radius: 5px;
}
.Avatar {
    border-radius: 5px;
}
.module {
    border-radius: 5px;
}
.HeartAnimation {
    background-image: url(https://abs.twimg.com/a/1560978792/img/animations/web_heart_animation.png);
}
body.ms-windows {
    font-family: Arial,sans-serif;
}
.EdgeButton--tertiary {
    background-color: #fff;
    background-image: linear-gradient(0deg, #f5f8fa, #ffffff00);
    border: 1px solid #e3ebef;
    color: #292f33;
    line-height: 22px;
    padding: 6px 11.94px;
}
.EdgeButton--tertiary:hover {
    background-color: #e1e8ed;
    background-image: linear-gradient(0deg, #e1e8ed, #fff);
    border: 1px solid #e1e8ed;
    color: #292f33;
    line-height: 22px;
    padding: 6px 11.94px;
}
button.EdgeButton.EdgeButton--primary.EdgeButton--medium.button-text.following-text {
    background: #43aaf4;
    background-image: linear-gradient(0deg, #3290d9, transparent);
    border: 1px solid #3f87c5;
}
.EdgeButton--secondary{
    background-color: #fff;
    background-image: linear-gradient(0deg, #f5f8fa, #ffffff00);
    border: 1px solid #e3ebef;
    color: #292f33;
}
.EdgeButton--secondary:hover{
    background-color: #e1e8ed;
    background-image: linear-gradient(0deg, #e1e8ed, #fff);
    border: 1px solid #e1e8ed;
    color: #292f33;
}
button.EdgeButton.EdgeButton--secondary.EdgeButton--medium.button-text.follow-text::before {
    content: "\f175";
    font-family: rosetta;
    color: #1da1f2;
    top: 3px;
    font-size: 18px;
    line-height: 0px;
    font-weight: 500;
    position: relative;
}
span.user-actions-follow-button.js-follow-btn.follow-button {
    width: 99px;
    height: 36px;
}
button.EdgeButton.EdgeButton--secondary.EdgeButton--small.button-text.follow-text::before {
    content: "\f175";
    font-family: rosetta;
    color: #1da1f2;
    top: 5px;
    font-size: 17px;
    line-height: 0px;
    font-weight: 100;
    position: relative;
}
.follow-button .button-text.EdgeButton--medium, .edit-button .button-text.EdgeButton--medium {
    min-width: 99px;
}
.EdgeButton--medium {
    height: 36px;
}
button.EdgeButton.EdgeButton--secondary.EdgeButton--small.button-text.follow-text {
    padding: 4px;
}
.follow-button .button-text.EdgeButton--small, .edit-button .button-text.EdgeButton--small {
    min-width: 77px;
}
.ProfileHeading-content {
    border-radius: 5px 5px 0px 0px;
}
.ProfileCardMini-avatarImage {
    border-radius: 5px;
}
span.NewTweetButton-text::before {
    top: 5px;
    left: -6px;
    content: "\f029";
    font-family: rosetta;
    font-size: 22px;
    font-weight: 500;
    position: relative;
}
span.NewTweetButton-text {
    top: -2px;
    position: relative;
}
span.DMButton-text::before{
    top: 5px;
    left: -6px;
    content: "\f054";
    font-family: rosetta;
    font-size: 21px;
    font-weight: 500;
    position: relative;
}
span.DMButton-text {
    top: -2px;
    position: relative;
}
button.DMButton.u-sizeFull.u-textTruncate.js-tooltip.EdgeButton.EdgeButton--primary {
    height: 41px;
}
.ProfileHeading-toggleItem {
    font-weight: 100;
}
.u-boxShadow {
    box-shadow: 0 0.3px 0px 0 rgba(0,0,0,0.25);
}
.nav>li>a .text {
    font-weight: 100;
}
.ProfileHeading-toggleItem.is-active, .ProfileHeading-toggleItem.is-active:hover, .ProfileHeading-toggleItem.is-active:focus {
    font-weight: 100;
}
.Icon--lightBulbOn::before {
    font-family: "edgeicons" !important;
}
.Icon--lightBulbOff::before {
    font-family: "edgeicons" !important;
}
.top-timeline-tweetbox .timeline-tweet-box {
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
}
.stream-end-item, .stream-end, .stream-loading, .stream-placeholder {
	border-radius: 0px 0px 6px 6px;
}
.ProfileCard {
	border-radius: 6px;
}
.ProfileCard-avatarImage {
	border-radius: 5px;
}
.ProfileCard-avatarLink {
	border-radius: 5px;
}
.Icon--periscopeBadge::before {
	font-family: "edgeicons" !important;
}
}
