}

.jp-details,
.jp-playlist {
	position: relative;
	-webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	border-radius: 2px;
	margin: 4px 0;
	background-color: #e1e1e1;
	border: 1px solid #726b79;
	background-image: -ms-linear-gradient(top, #f6f6f6 0%, #e1e1e1 100%);
	background-image: -moz-linear-gradient(top, #f6f6f6 0%, #e1e1e1 100%);
	background-image: -o-linear-gradient(top, #f6f6f6 0%, #e1e1e1 100%);
	background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, #f6f6f6), color-stop(1, #e1e1e1));
	background-image: -webkit-linear-gradient(top, #f6f6f6 0%, #e1e1e1 100%);
	background-image: linear-gradient(to bottom, #49444e 0%, #39353ef0 100%);
}

div.jp-type-playlist div.jp-playlist a {
    color: #aeb2b7;
    text-decoration: none;
}

div.jp-type-playlist div.jp-playlist a.jp-playlist-current {
    color: #4ba5ce;
}

div.jp-type-playlist div.jp-playlist a:hover {
    color: #458fce;
}

.jp-playlist li {
    padding: 5px 0 4px 20px;
    border-bottom: 1px dotted #726b79;
}

.jp-interface {
    background-image: linear-gradient(to bottom, #49444e 0%,#49444e 50%,#49444e 51%,#49444e 100%);
    border: 1px solid #726b79;
}

.jp-audio .jp-time-holder {
    color: #aeb2b7;
}

.jp-audio {
    color: #408db1;
}

.jp-controls button {
    filter: invert(1);
}

.jp-volume-controls button {
    filter: invert(1);
}

.jp-audio .jp-progress {
    filter: contrast(0.5);
}

.jp-volume-bar {
    filter: contrast(0.5);
}

.spoiler {
    padding: 4px 4px 5px 7px;
    position: relative;
    border: solid 2px;
    border-color: #726b79 #645e6b #645e6b #726b79;
}

.spoiler > input:checked + .box > .text {
    border-top: #726b79 1px dashed;
}

h1, h2, h3, h4, h5, h6 {
    color: #85808a;
}


.comment {
    background-color: #58545d;
}

.comment {
    border-bottom: 1px solid #7c7386;
}


#footer {
    border-top: #3b3740 5px solid;
    background: linear-gradient(to bottom, rgb(43, 40, 45) 0%,rgb(43, 40, 46) 46%);
}


.dropdown-menu {
    background-color: #635e69;

}

.item-list li .author {
    color: #d6bad8a1;
    
}

.navbar .nav>li>a {
    color: #afabb3;

}

.navbar .nav > li:hover > a, .navbar .nav > li > a:focus, .navbar .nav li.dropdown.open > .dropdown-toggle, .navbar .nav li.dropdown.active > .dropdown-toggle, .navbar .nav li.dropdown.open.active > .dropdown-toggle, .navbar .nav > .active > a, .navbar .nav > .active > a:hover, .navbar .nav > .active > a:focus {
