}

.comment.comment-new .text {
    background-color: #736b7b;
}

.comment.comment-self {
    background-color: #525153;
}

.comment.comment-self .text {
    background-color: #525153;
}

.form-actions {
    background-color: #615d65;
    border-top: 1px solid #615d65;
}

.wrapper-content.wrapper-content-dark {
    background-color: #7e7d80;
}

