css:
@-moz-document url-prefix("https://www.lurker.pl/") {
body {
    font-size: 15px;
    line-height: 1.4;
}

main {
    margin: 10px 0;
}

/* container na sortowanie i padding */
main#home .mt--6 {
    margin-top: -6px;
    padding: 8px 0;
}

/* prawa kolumna */
main#home .mt {
    margin-top: 36px;
}

/* karteczka */
main#home .mb {
    margin-bottom: 8px;
}

/*zmiana wielkości fontu w komentarzach */
.newcomment__body,
.singlePost__body {
    font-size: 16px;
}

/* zwiększenie marginesu akapitu */
.newcomment__body p,
.singlePost__body p {
    margin-top: 8px;
    margin-bottom: 16px;
    line-height: 25px;
}

/* zmiana stylu fonta dla details wpisu (autor, data...) -> UWAGA -> INNY KOLOR DLA NIGHTMODE */
.mikropost__details,
.mikropost__details a,
.mikropost__details span {
    color: #6d7f9b !important;
    font-size: 12px;
    line-height: 15px;
    font-weight: 400;
    opacity: .7;
}
.mikropost__details a:first-of-type {
    color: #fff!important;
    opacity: 1!important;
}

.mikropost__details a:nth-of-type(2) {
    opacity: 1!important
}
.newpost__commentsCount {
    opacity: 1!important;
}


/*Nie umiem flex, ale chcailem rozszerzyc body wpisu -> trzeba zmienić na 12 kolumn */
.newpost__partB.mikro > div > div > div > div {
    -ms-flex: 0 0 100%;
    -webkit-box-flex: 0;
    flex: 0 0 110%;
    max-width: 110%;
}
/*border sekcji z avatarem przy wpisie */
.nightMode .newpost__partA.mikro {
    border-color: #192029;
}

/* btn DODAJ WPIS */
.cta {
    margin-bottom: 16px;
}

.sidebar__hashSearch h5 {
    margin: 8px 0;
}

.newpost__partB .justify-content-center {
    justify-content: normal!important;
}

.rawPagination {
    padding-right: 0;
}

.el-pagination {
    padding: 2px 0;
}

.el-card .list__item {
    display: inline-block;
    width: 50%;
    font-size: 10px;
    line-height: 8px;
}

main#list .mt-10 {
    margin-top: 4px;
    margin-bottom: 4px;
    padding-right: 4px;
    padding-left: 4px;
}

.el-card__body a {
    color: #aaa;
}

.el-card__body a:hover {
    color: #2780e3;
}

main#notifications .mb {
    margin-bottom: 12px;
}

main#notifications .pt {
    padding-top: 4px;
}

main#notifications .notification__buttons {
    margin-top: 4px;
}

main#notifications .mt {
    margin-top: 8px;
}

/*section.newcomment .newcomment_footer {
    border-bottom: 1px solid #2f3844;
    
}*/
.newcomment__info {
    height: 30px;
}

.mikropost__content {
    min-height: 0;
}

.el-tag {
    font-size: 9px;
    line-height: 16px;
    font-weight: 700;
    letter-spacing: 0.5px;
}

/*wprowadzone koniec */
.newcomment__avatar img {
    width: 40px;
    height: 40px;
}

.newcomment_footer {
    margin-top: -8px;
}

.newcomment_atop {
    margin-top: 24px;
}

/* kolorki */
.nightMode .header__firstLine {
    background: #192029;
    border-bottom: 1px solid #11181e;
    ;
}

body.nightMode {
    background: #11181e;
    ;
}

.nightMode .bgWhite {
    background: #192029;
}

.nightMode .appHeader {
    background: #192029;
}

.nightMode .header__secondLine a.isActive,
.nightMode .header__secondLine a.isActive:focus {
    background: #192029;
}

.nightMode .header__secondLine a {
    background: #11181e;
    border-color: #192029;
    border-radius: 4px;
}

.nightMode .el-dialog__body,
.nightMode .el-dialog__footer,
.nightMode .el-dialog__header {
    background: #192029!important;
}

.nightMode .singlePost__body {
    background: #11181e;
    ;
}

.sidebar .el-button--primary {

    color: #409eff;
    border: solid 3px #409eff;

    border-top-color: rgb(64, 158, 255);
    border-top-style: solid;
    border-top-width: 3px;
    border-right-color: rgb(64, 158, 255);
    border-right-style: solid;
    border-right-width: 3px;
    border-left-color: rgb(64, 158, 255);
    border-left-style: solid;
    border-left-width: 3px;

    border-top: 0;
    border-right: 0;
    border-left: 0;
    box-shadow: none;
    text-transform: uppercase;
    letter-spacing: 1.2px;
    background: none;
    margin-bottom: 30px;
    padding: 12px 16px!important;
}

.nightMode .el-button--primary:hover {
    background: #2780e31a;
    border-color: #2780e3;
    color: #fff;
}

.newpost__thumbnail {
    border-left: 2px solid #e4e7ed;
}
.nightMode .newpost__thumbnail {
    border-color: #11181e;
    ;
}

.nightMode .footerSection {
    color: #eee;
    background: #192029;
}

.nightMode .prefooter {
    color: #ccc;
    background: #1c2027;
}

/*edytor*/
.nightMode .el-dialog__body,
.nightMode .el-dialog__footer,
.nightMode .el-dialog__header {
    background: #192029;
}
.nightMode .el-textarea textarea {
    background: #11181e;
    ;
}
.nightMode .leditor__textarea.el-textarea textarea:hover {
    background: #11181e;
    ;
    border-color: #409eff;
}


/* Przycisk Publikuj na bialo */
.nightMode .el-button--primary {
    color: #fff;
}

/*Przesuwaki */
.el-switch__core {
    background: #11181e;
    ;
    border: 1px solid #1e2733;
}
.el-switch__core::after {
    background-color: #409eff;
    top: 2px;
    width: 14px;
    height: 14px;
}

.el-switch.is-checked .el-switch__core {
    border: 2px solid #409eff;
    background: none;
}

.el-switch.is-checked .el-switch__core::after {
    top: 1px;
    opacity: 1;
}



/*poprawki pojedynczych spraw */
.cta--mini:hover {
    margin-bottom: 16px;
}
.mikroContent {
    align-items: first baseline;
}
.mikropost__scoreBox .imgz {
    max-width: 50%;
}

.nightMode .singlePost__closeIcon {
    background: #11181e;
    ;
}
.cta--mini,
.cta--mini:active,
.cta--mini:focus,
.cta--mini:hover {
    padding: 1px 14px;
}
.mikropost__details span:hover {
    color: #6d94d1 !important
}
.newpost__info span:hover,
.newpost__links i:hover {
    color: #6d94d1 !important
}
.newpost__info i:hover {
    color: #6d94d1 !important
}
.nightMode .newpost__downvote:hover,
.nightMode .newpost__upvote:hover {
    color: #6d94d1 !important
}

.newpost__upvote,
.newpost__downvote {
    font-size: 20px;
}

.nightMode .el-select .el-input__inner {
    background: #11181e;
    ;
}
.el-select-dropdown__item {
    color: #fff;
}
.el-select-dropdown {
    background-color: #11181e;
    ;
    border: 1px solid #1e2733;
}
.el-select-dropdown__item.hover,
.el-select-dropdown__item:hover {
    background-color: #1C2027;
}

.nightMode .el-button {
    background: #11181e;
    ;
    border-color: #2f3844;
    color: #ccc;
}
.nightMode .el-button:hover {
    background: #2780e31a;
    border-color: #2780e3;
    color: #fff;
}
.leditor__options i:hover {
    color: #6d94d1 !important;
}
.leditor__options span:hover {
    color: #6d94d1 !important;
}
.newcomment_footer {
    opacity: 1;
}
.nightMode .newcomment_footer button {
    color: #fff;
}


/*buttony primary */
.nightMode .el-button--primary {

    color: #409eff;
    border: solid 2px #409eff;

    border-top-color: rgb(64, 158, 255);
    border-top-style: solid;
    border-top-width: 3px;
    border-right-color: rgb(64, 158, 255);
    border-right-style: solid;
    border-right-width: 3px;
    border-left-color: rgb(64, 158, 255);
    border-left-style: solid;
    border-left-width: 3px;

    border-top: 0;
    border-right: 0;
    border-left: 0;
    box-shadow: none;
    text-transform: uppercase;
    letter-spacing: 1.2px;
    background: none;
    margin-bottom: 30px;
    padding: 4px 16px;
    text-align: center;
}


/* USER PROFILE */
.nightMode .profileTop {
    background: #192029;
}

.nightMode .bgz {
    background: #192029;
}


/* post odstępy */
.singlePost__body p {
    margin-top: 2px;
    margin-bottom: 0px;
    line-height: 22px;
}

#postBodySingle p:last-child {
    margin-bottom: 16px;
}


/*te same odstępy ale na mikro liście */
.mikropost__content p {
    margin-top: 2px;
    margin-bottom: 0px;
    line-height: 22px;
}

