}

/* odstęp od post-details */
.mikropost__details {
    margin-bottom: 8px;
}

/* Dropdown */
.el-dropdown-menu {
    background-color: #11181e;
    border: 1px solid #192029;
}

.el-dropdown-menu__item {
    color: #FFF;
}

.el-dropdown-menu__item:focus,
.el-dropdown-menu__item:not(.is-disabled):hover {
    background-color: #192029;
}

/* Paginacja */
.nightMode .el-pager li.number {
    color: #ddd;
}

/*Najnowsze komenty */
.recentComments a {
    color: #6d7f9b;
}

.recentComments .titled {
    color: #409eff!important;
    line-height: 24px;
}

/*    STYLIZACJA
        KOMENTÓW */
