css:
@-moz-document url-prefix("https://moodle.kftg.ch/") {
body {
    color: white;
    background-color: #323232;
}

.card-body {
	
    background-color: #323232;
}

#page-wrapper nav{
	background-color: #323232;
}

.bg-white {
    background-color: #202020 !important;
}

.navbar-light .navbar-brand {
    color: #999;
}

.btn {
	background-color: #323232;
	color: #999;
}
}

@-moz-document url("https://moodle.kftg.ch/login/index.php") {
.card-body, .userprofile .profile_tree section {
    background-color: #646464;
    color: white;
}

.card-header {
	background-color: #323232;
}
}

@-moz-document url("https://moodle.kftg.ch/my/") {
.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {

    color: black;
    background-color: #999;
    border-color: #dee2e6 #dee2e6 #999;

}
}
