css:
@-moz-document url-prefix("https://coursehunters.net") {
/* i really want this to be global */
/*page course*/
.course-wrap {
	height: 210px;
	overflow-y: auto;
	box-shadow: 0 3px 21px rgba(0, 0, 0, 0.2);
}

/*title*/
.jw-title {
	display: block;
	padding-top: 0;	
	margin-top: -45px;
	font-size: 12px;
}
.jw-title-primary,
.jw-title-secondary {
	background: linear-gradient(to right, #ce6df0, #6c49e3);
	-webkit-text-fill-color: transparent;
	-webkit-background-clip: text;
	color: #ce6df0;
}
.jw-title-primary:before {
	content: '▸▸▸';
	color: #ff0;
	font-size: 20px;
	margin-right: 0.5em;
}
.jw-title-primary {
	padding-left: 0;
}
#myElement {
	overflow: visible;
}
/** .player-full **/
.player-full .lessons-list{
	max-height: none;
	column-count:4;
	padding-right:0;
}
.player-full .lessons-name{
	font-size: 12px;
}

.player-full .lessons-title{
	line-height: 1em;
}

@media (max-width: 1024px){
	.player-full .lessons-list{
		column-count:3;
	}
}
@media (max-width: 800px){
	.player-full .lessons-list{
		column-count:2;
	}
}
/* Body */
.hero-title {
    color: #ffffff !important;
}
.course-description-wrap {
     color: #9F10A1 !important;
    font-weight: bold;
}
.course-description {
     color: #ffff !important;
}
body {
    background: #000000;
}
.container h2 {
    color: #ffffff;
}
.mc-comments div {
    color: #ffffff;
}
.mc-comments .mc-comment-msg {
    color: #ffffff !important;
}
.mc-comments .mc-comment-username {
    color: #780AB5 !important;
}
}
