css:
@-moz-document url-prefix("https://vrchat.com/home"), url-prefix("https://vrchat.com/home"), url-prefix("https://www.vrchat.com/home"), url-prefix("https://www.vrchat.com/home") {
.fixed-top{
    background-color:#111111!important;
    box-shadow:none!important;
}

.btn-secondary{
    background:rgba(0,0,0,0.4);
    border-radius:2px;
    border:none!important;
}

.leftbar, .rightbar{
    background:rgba(0,0,0,0.4)!important
}

.home-content{
    background:transparent;
}

body{
    background-image:url("https://bocchi.is-pretty.cool/45KM5dj.png")!important;
    background-size:cover!important;
    background-repeat:no-repeat;
}

.card, input{
    background:rgba(0,0,0,0.5)!important;
    color:#95E9F6!important;
}

.btn-primary, .btn-outline-secondary, .btn-outline-primary{
    background:rgba(17, 186, 213,0.2);
    color:#1FD1ED;
    box-sizing:border-box;
    border:none!important;
}

.alert{
    background: #10279A;
    border:none!important;
    color:white!important;
    margin-top:1%;
}
}
