css:
@-moz-document url-prefix("https://www.roblox.com") {
.rbx-header {
    opacity: 1;
    background: rgba(0,0,0, .6);
    backdrop-filter: blur(15px);
    box-shadow: 0 1px 6px 0 rgba(32,33,36,0.9);
}
.chat-main {
    opacity: 1;
    background: rgba(255,255,255, .7);
    box-shadow: 0 0px 6px 0 rgba(32,33,36,0.7);
    backdrop-filter: blur(15px);
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
}

.rbx-tabs-default, .rbx-tabs-horizontal .nav-tabs, .rbx-tabs-vertical ul {
    box-shadow: 0 1px 8px 0 rgba(32,33,36,0.3);
    border-radius: 10px;
}

.carousel .carousel-thumb {
   border-radius: 5px;
   box-shadow: 0 1px 8px 0 rgba(32,33,36,0.3);
}

.form-control{
    border-radius: 10px;
}

.input-group .input-group-btn:last-child .input-addon-btn {
    border-radius: 0 10px 10px 0;
}

.btn-common-play-game-lg, a.btn-common-play-game-lg {
	border-radius: 15px;
}

.chat-windows-header {
    color: rgba(20,20,20,1)
}

.modal-dialog .modal-content {
    border-radius: 10px;
}

.chat-search .chat-search-input {
    background: ffffff50;
}

.chat-main .chat-header {
    background-color: #00a2ff6b;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
}

.rbx-left-col {
    opacity: 1;
    background: rgba(255,255,255,.6);
    box-shadow: 0 1px 8px 0 rgba(32,33,36,0.3);
    backdrop-filter: blur(15px);
}
.border-bottom {
    border-bottom: none
}


.alert-info {
    opacity: 1;
    background: rgba(246, 136, 2,.7);
    box-shadow: none;
    backdrop-filter: blur(15px);                                  
}

.modal-backdrop {
    backdrop-filter: blur(5px)
}

.section-content {
    box-shadow: 0 1px 8px 0 rgba(32,33,36,0.3);
    border-radius: 10px;
}

.game-card-containter {
    box-shadow: 0 1px 8px 0 rgba(32,33,36,0.3);
}

.gotham-font, .gotham-font .h1, .gotham-font .h2, .gotham-font .h3, .gotham-font .h4, .gotham-font .h5, .gotham-font .h6, .gotham-font h1, .gotham-font h2, .gotham-font h3, .gotham-font h4, .gotham-font h5, .gotham-font h6 {
    text-shadow: 0px 1px 3px rgba(0, 0,0, 0.3);
}
}
