css:
@-moz-document domain("mb.srb2.org") {
.page {
    background: #191919;
}

/*==========================*/
/* Remove Background Panels */
/*==========================*/

table[id*=post] tbody tr td, table#threadslist tbody tr td, table.tborder, table.tborder tbody tr td.alt1, table.tborder tbody tr td.alt2, table.tborder tbody tr td.alt1Active, div.block_row, td.thead, td.tfoot, .navbar {
    background: #191919;
    border: none !important; }

/* Alternating Colors Per Post */

tbody[id*=collapseobj_forumbit] tr [class*=alt] {
    background: #191919;
    border: none !important; }

table#threadslist tbody[id*="threadbits_forum"] tr:nth-child(even) [class*=alt], tbody[id*=collapseobj_forumbit] tr:nth-child(even) [class*=alt], table.tborder tbody tr:nth-child(even) {
    background: #232323 !important; }

/*====================*/
/* Image Buttons Dark */
/*====================*/

img[id*="collapseimg"], img[title="Post New Thread"], img[title="Reply"], img[title="Edit/Delete Message"], img[title="Reply With Quote"], img[title="Multi-Quote This Message"], img[title="Quick reply to this message"]  {
    filter: grayscale(1) brightness(0.75); }

/*=======================*/
/* Image Resize Bar Dark */
/*=======================*/

table.ncode_imageresizer_warning {
    background: #151515;
    color: #DDDDDD;
    border: none;
    padding-left: 3px; }

table.ncode_imageresizer_warning td.td2 {
    background: #151515; }

table.ncode_imageresizer_warning td.td1 {
    display: none; }

/*===================================*/
/* Spoiler Tags / Addon Details Dark */
/*===================================*/

.alt2 {
    background: #151515;
    border: 1px solid #151515 !important; }

/*====================*/
/* Horizonal Rule Fix */
/*====================*/

hr {
    background: #B1B1B1;
    border: none ;
    height: 1px; }

.bigusername[style*=border-bottom] {
    font-weight: 700;
    font-size: 14pt;
    padding-bottom: 4px;
    border-bottom: 2px solid rgba(0,0,0,0)!important; }

/*=========================*/
/* Ugly AF Colored Banners */
/*=========================*/

.menu {
    background: #232323 !important; }

.menu a {
    border: 0px solid #000000; }

.menu a:hover {
    background: #333333; }

.tcat, .vbmenu_control, .fieldset {
    background: #191919 !important; }

/*=====================*/
/* Fix Lack of Padding */
/*=====================*/

/* Make Header In-Line With Rest of Page */

body table[height="180"] {
    background: #191919 !important; }

body table[height="180"] img {
    margin: 0 0 0 2.5%;
    padding: 0 15px 0 15px; }

.menu ul:first-child {
    margin: 0 0 0 2.5%; }

/* Addon/Developer Info Fix */

.tcat {
    margin: 5px 0 5px 0 !important; }

.navlist div {
    margin: 0 0 0 1px; }

.navlist span {
    margin: 0 0px 2px 0;
    text-align: left !important; }

/* Remove Weird Master Server Tab Spacer */

.tab_header {
    display: none; }

/* Stupid Fucking Post Attribute Image Vertical Centering */

span[style*=float] {
    transform: translateY(-2px); }

span[style*=float] img {
    transform: translateY(3px); }

/* Firefox-Specific Padding Fix */

.menu a {
    width: 90px; }

/*===============================*/
/* Misc. Black on Black Text Fix */
/*===============================*/

.panel {
    color: #D4D4D4; }

.servername-background {
    background: rgba(0,0,0,0) !important;}

/*===========================*/
/* Quick Reply / Post Editor */
/*===========================*/

.panel, .vBulletin_editor, .panelsurround, .controlbar {
    background: #191919;
    border: none; }

#vB_Editor_001_textarea {
    width: 595px;
}

.panel .smallfont {
    margin: 0 0 0 7px;
    color: #C8C8C8; }

.panel .smallfont[style="float:right;"] {
    float: left; }

legend, fieldset div label {
    color: #C8C8C8; }

.fieldset {
    border-color: #C0C0C0;
    margin: 0 0 0 7px !important;
    width: 94.6%; }

.imagebutton {
    background: rgba(0,0,0,0);
    filter: invert(1); }

}

@-moz-document url-prefix("https://www.srb2.org") {
/*================*/
/* SRB2.org Fixes */
/*================*/

body {
    background: #191919}

#menu {
    
}

.post {
    background: #1f1f1f;
    border-radius: 6px;
    margin: 10px;
    padding: 10px;
}

}
