css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#urlbar[level="high"] > .autocomplete-textbox-container
   { background-color: rgba(128,128,255,.3) !important; color: lightblue !important;}
#urlbar[level="high"] > .autocomplete-history-dropmarker {
  background-color: rgba(128,128,255,.3) !important;}

#urlbar[level="low"] > .autocomplete-textbox-container
   { background-color: rgba(128,255,255,.3) !important; color: gold !important; }
#urlbar[level="low"] > .autocomplete-history-dropmarker {
  background-color: rgba(128,255,255,.3) !important;}

#urlbar[level="broken"] > .autocomplete-textbox-container
   { background-color: rgba(255,128,128,.3) !important; color: pink !important; }
#urlbar[level="broken"] > .autocomplete-history-dropmarker {
  background-color: rgba(255,128,128,.3) !important;}

#main-window #urlbar
	{-moz-background-clip: padding;
	border: 2px solid !important;
	-moz-border-radius: 3px;
	-moz-border-top-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) rgba(255, 255, 255, 0.95) !important;
	-moz-border-right-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) rgba(255, 255, 255, 0.95)!important;
	-moz-border-bottom-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) rgba(255, 255, 255, 0.95)!important;
	-moz-border-left-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) rgba(255, 255, 255, 0.95)!important;
	-moz-appearance: none !important;
       -moz-border-radius: 8px !important;
        height: 23px !important;
        background: transparent !important;}

#main-window #searchbar
	{-moz-background-clip: padding;
	border: 2px solid !important;
	-moz-border-radius: 3px;
	-moz-border-top-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) rgba(255, 255, 255, 0.95) !important;
	-moz-border-right-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) rgba(255, 255, 255, 0.95)!important;
	-moz-border-bottom-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) rgba(255, 255, 255, 0.95)!important;
	-moz-border-left-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) rgba(255, 255, 255, 0.95)!important;
	-moz-appearance: none !important;
       -moz-border-radius: 8px !important;
        height: 23px !important;
        background: transparent !important;}

#urlbar , #searchbar *{
  -moz-appearance: none !important;
  background: transparent !important;
  color: #fff !important;}


.search-go-button, .search-go-button-stack, #go-button {
	display: none !important;
}
#identity-box {
	-moz-margin-start: -1px !important;
	border-right: none !important;
}
#identity-box > hbox {
	background: none !important;
	color: rgba(0,0,0,.6) !important;
	border: none !important;
	margin: -10px 0 -10px 0 !important;
	padding: 10px 0 10px 0 !important;
}
.searchbar-engine-button{
	background: none !important;
	border-right: none !important;
}
