@-moz-document domain("jandan.net") {
.row {
	margin:5px;
	padding:5px;
	box-shadow: 0 5px 10px 1px #00000054;
	transition: 0.4s;
	border-radius: 1%;
	background-image: -webkit-linear-gradient(left bottom, #484747 0%, #8a6161 100%);
	color: white;
}
.row:hover {
	transform: translate(0, -20px);

	background-image: -webkit-linear-gradient(left bottom, #9f9f9f 0%, #ae3e3e 100%);
}
.row:focus {
	transform: scale(1.2);
}
}
