css:
@-moz-document domain(fastmail.com) {
	body {
    /*background: url('https://lh3.googleusercontent.com/-_ocRJ5kXL9w/XC6Fio8CVNI/AAAAAAAAGLk/P3grI7vB29QjUrFzii2uh_YubkC46m73QCPgBGAYYCw/w1920-h1441-p-e365-fVignette=1,0,1.4,0,000000:Soften=1,0,0:-k-no/PC285371-25.jpg');*/
	background: url('/*[[background]]*/');
    background-size: cover;
}
#mailbox, .v-Page--sidebar, .v-Page--contrast {
    background: #00000033;
}
.v-PageHeader, .v-Quota, .v-Page--contrast .v-Page-viewOptions, .v-MessageCard-time, .v-MessageCard-draft, .v-MessageCard-showMore, .v-MessageCard > .v-Toolbar {
    background: transparent;
}
.v-MailboxItem.is-read {
    background-color: rgba(20, 20, 20, 0.8);
}
.v-MailboxItem.is-unread {
    background-color: rgba(51, 51, 51, 0.902);
}
.v-Sidebar-footer, .v-Split--left > .v-HierarchyPane > .v-Page > .v-Page-content, .v-Split--left:not(.is-hidden) > .v-Page > .v-Page-content, .v-Split--left:not(.is-hidden) > .v-Page > .v-Page-viewOptions {
    border-right: none;
}
.v-PageHeader .v-Text {
    background: #ffffff33;
    color: rgba(255, 255, 255, 0.87);
    border-radius: 12px;
    transition: background 100ms ease-in, width 100ms ease-out;
}
.v-PageHeader .v-Text.is-focused {
    background: #fff;
    border-radius: 12px 12px 0 0;
}
.v-AutoComplete, .v-AutoCompleteOption {
    border-radius: 0 0 12px 12px;
}
.v-PageHeader .v-Button, .v-Button--standard {
    border-radius: 24px;
    padding: 0 1rem;
}
.app-source:hover {
    background-color: rgba(255, 255, 255, 0.30);
    border-radius: 0 16px 16px 0;
    color: #fff !important;
}
.v-MailboxSource-badge {
    background: none;
    border: none;
}
.v-Thread {
    max-width: none;
}

.app-contentCard {
    border: none;
    border-radius: 0;
}
.u-color2, .u-pageTextColor {
    color: #202124;
}
.u-unimportant, .u-subtleLink {
    color: #555;
}
.v-Message-details {
    border-bottom: 1px solid rgba(100, 121, 143, 0.122);
}
.v-Toolbar--standard .v-Button {
    align-items: center;
    border: none;
    display: inline-flex;
    justify-content: center;
    outline: none;
    position: relative;
    z-index: 0;
    -moz-osx-font-smoothing: grayscale;
    font-family:'Google Sans', Roboto, RobotoDraft, Helvetica, Arial, sans-serif;
    font-size: .875rem;
    letter-spacing: .25px;
    background: none;
    border-radius: 4px;
    box-sizing: border-box;
    color: #5f6368;
    cursor: pointer;
    font-weight: 500;
    height: 36px;
    outline: none;
    padding: 0 16px;
    padding-left: 16px;
    box-shadow: inset 0 0 0 1px #dadce0;
    min-width: 104px;
    padding-left: 12px;
    -moz-user-select: none;
    user-select: none;
    margin-right: 12px;
    text-decoration: none;
}
#v223 {
    padding: 0 0 1rem 1.5rem;
}
.v-PopOver {
    background: #fff;
    color: #202124;
    box-shadow: 0 8px 10px 1px rgba(0, 0, 0, 0.14), 0 3px 14px 2px rgba(0, 0, 0, 0.12), 0 5px 5px -3px rgba(0, 0, 0, 0.2);
}
.v-PopOver-triangle {
    background: transparent;
}
.v-MenuOption.is-focused > .v-Button, .v-MenuOption > .v-Button:active, .v-MenuOption > .v-MenuButton.is-active {
    background: #eee;
}
.v-MenuOption > .v-Button {
    padding-bottom: 6px;
    padding-top: 6px;
    padding-left: 48px;
    padding-right: 48px;
}
.v-Text {
    background-color: #fff;
}
.v-MainMenu {
    background: #eee;
}
.v-MainMenu-apps {
    background: #fff;
    border-right: 0.0625rem solid #ddd;
}
.v-MainMenu-apps a:hover {
    background-color: #eee;
}
.v-StatusBar {
    background: transparent;
    border: none;
}
.v-Text-input,
.v-EmailText.is-focused > .v-EmailText-input {
    color: #202124;
}

.v-ComposeAttachment-download,
.v-Thread-title,
.app-contentCard {
    color: #fff;
}
.v-Thread-title, .app-contentCard {
    background: #fff;
    color: #202124;
}

.v-Toolbar {
    color: #fff;
}
}
