css:
@-moz-document domain("foreignpolicy.com") {
#wrapper,
#theatre,
#content-lz {
    width: 100% !important
}

.blog_entry {
    margin-left: 1.5% !important;
    width: 98% !important;
}

#content-lz {
    padding: 0px !important;
}

DIV[style="margin: 20px 0pt;"][class="content"], .the-tags {
    margin: 0px 0pt !important;
}

P {
    line-height: normal !important;
}

.blog_entry {
    background-image: none !important;
    margin-bottom: 0px !important;
    padding-bottom: 0px !important
}

#flag,
#header,
#featured-blog,
#banner-blog,
#aux-rail,
.agg_post_meta,
.subjects,
.smallgray,
.blog-hed,
HR {
    display: none !important
}

H2 {
    display: none !important
}

#mb-body .blurb {
    font-family: Arial, Helvetica, sans-serif !important;
    font-size: 1em !important;
    line-height: normal !important;
}
body, .home-region-7, .home-region-6, .home-region-5, .home-region-4, .home-region-3, .home-region-2, .home-region-1, .fyre * {
	background-color: #333 !important;
    color: #b9a993 !important;
    border-color: #222 !important;
}
body::before, .sticky_content, .site-header.header-alt-layout, .site-footer-wrapper, div.taboola, .heading-container, .home-ad-region-2, .FP-mobile-ad_fixedbottom,.updated_paywall, div.channel-sidebar-big-box-ad:nth-child(1), .ad-container._300 {
    display:none !important;
}
.bio-no-photo, .related-articles, .excerpt-content--list, .sidebar, .site-header, .header-menu-item, .navlink.-emph, .internal-ad-region-1, .internal-ad-region-2, .internal-ad-region-3, .internal-ad-region-4 {
	background-color: #252525 !important;
    border-color: #555 !important;
}
a, .fyre a {
	color: #559c7a !important;
	text-decoration: none;
}
.the-tags > .title, .the-tags, .title, .fp-red {
	color: #8ba446 !important;
	font-weight: 800;
}
h1, h2, h3, h4, h5, h1 b, h2 b, h3 b, h4 b, h5 b {
    color: #b9a993 !important;
}

.sidebar .date-time, .sidebar ,.date-time, .dek-heading {
    color: #888 !important;
}
.post-content > .sidebar {
	display: block;
}
.post-content > .article {
	padding: 0 12px 0 25px;
}
.post-content {
	padding: 0;
}
.related-articles {
	padding: .75em;
    margin: .75em 0;
}
.site-header .logo svg path {
	fill: #8ba446;
}
.trending-articles .heading, .the-comments, .button {
	background-color: #333 !important;
	color: #8ba446 !important;
    border-color: #555 !important;
}
.feature-template .department-name h3 {
    background-color: #222 !important;
	color: #8ba446 !important;
    border-color: #555 !important;
}
.expando-search {
    background-color: #333 !important;
	color: #b9a993 !important;
}
.expando-search .search-icon {
	stroke: #8ba446 !important;
	stroke-width: 1.5;
}
.search:focus {
	background-color: #333 !important;
	color: #8ba446 !important;
}
.fp-trending-wrapper .fp-trending-content .fp-trending-count {
	color: #b9a993 !important;
}
.sidebar {
	padding-left: 12px ;
	padding-right: 10px ;
    border-left: 1px solid #555 !important;
}
.excerpt-content, .trending-articles, .the-tags, .meta-data, .m-block-1, .m-block-2, .m-block-3, .m-block-4, .m-block-parent, .e-block-1, .e-block-2, .e-block-3, .e-block-4, .e-block-parent, .g-block-1, .g-block-2, .g-block-3, .g-block-4, .g-block-parent, .a-block-1, .a-block-2, .a-block-3, .a-block-4, .a-block-parent, .b-block-1, .b-block-2, .b-block-3, .b-block-4, .b-block-parent, .c-block-1, .c-block-2, .c-block-3, .c-block-4, .c-block-parent, .d-block-1, .d-block-2, .d-block-3, .d-block-4, .d-block-parent, .d-block-0, .c-block-0, .b-block-0, .a-block-0, .e-block-0, .f-block-0, .g-block-0, .a-block-wrapper, .b-block-wrapper, .c-block-wrapper,.d-block-wrapper,.e-block-wrapper,.f-block-wrapper,.g-block-wrapper, .top-ten-stories li, li, ul {
    border-color: #555 !important;
}

.home-region-4 {
	margin: 0;
	padding: 0;
}
.fyre-editor .fyre-editor-toolbar .goog-toolbar > div {
        background-color: #333;
        border-bottom: 1px solid #222;
        border-right: 1px solid #222;
        color: #b9a993;
        background-image: -webkit-gradient(linear, left top, left bottom, from(#333), to(#222));
        background-image: -webkit-linear-gradient(top, #333, #222);
        background-image: -moz-linear-gradient(top, #333, #222);
        background-image: -o-linear-gradient(top, #333, #222);
        background-image: -ms-linear-gradient(top, #333, #222);
        background-image: linear-gradient(top, #333, #222);
}
html {
	font-size: x-large;
}
}
