css:
@-moz-document url(https://www.saltybet.com/) {
    body {
        background-color: black !important;
    }
    #header, #chat-wrapper, #sbettorswrapper, #footer {
        display: none !important;
        visibility: collapse !important;
    }
    html * {
        font-size: x-large !important;
    }
    #stream {
        max-width: none !important;
        width: 100% !important;
        top: 5px !important;
        left: 0px !important;
        right: 0px !important;
    }
    #bottomcontent {
        width: 100% !important;
    }
}
