css:
/* i really want this to be global */
[aria-role=img],
.image {
    max-width: 100% !important;
    height: auto !important;
}
[aria-role=img] {
    padding-top: 100%
}

.playableTile__artwork .image,
.sound__coverArt .image {
    transform: scale(0);
}

.playableTile__artwork .m-loaded,
.sound__coverArt .m-loaded {
    transform: scale(1);
    animation: appear-load .4s
}

.playableTile__artwork,
.sound__coverArt {
    box-shadow: inset 0 0 1px 0 currentColor;
    animation: appear-load 1s;
    overflow: hidden;
}

.dialog {
    border: none;
    animation: appear-load-dialog .4s;
}

.soundBadgeList,
.badgeList,
.user-badge {
    overflow: visible;
}

.badgeList > ul {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}

.badgeList__item:empty {
    display: none;
}
.badgeList__item {
    min-width: 115px;
}


.playControls__queue {
    max-width: 100%;
    bottom: 55px
}

.tileGallery__sliderPanelSlide,
.userBadge,
.soundBadge,
.badgeList__item,
.sound__coverArt {
    transition: transform 100ms;
    transform: scale(1);
}

.tileGallery__sliderPanelSlide:hover,
.userBadge:hover,
.soundBadge:hover,
.badgeList__item:hover,
.sound__coverArt:hover {
    transform: scale(1.025);
}

.playableTile__playButton,
.soundBadge__playButton,
.soundBadge__actions {
    display: block;
    visibility: unset !important;
    transition: opacity 500ms;
    opacity: 0;
}


@keyframes scale-load {
    0% {
        transform: scale(0)
    }
    100% {
        transform: scale(1)
    }
}

@keyframes appear-load {
    0% {
        opacity: 0
    }
    100% {
        opacity: 1
    }
}

@keyframes appear-load-dialog {
    0% {
        opacity: 0
    }
    100% {
        opacity: .95
    }
}

@keyframes fly-load {
    0% {
        transform: translatey(200px);
    }
    100% {
        transform: translatey(0);
    }
}

.playableTile__playButton:hover,
.soundBadge__playButton:hover,
.soundBadge:hover .soundBadge__actions,
.soundBadge.active .soundBadge__actions {
    visibility: unset !important;
    opacity: 1;
}
.playControls__bg
{
    display: none !important;
}

.volume__sliderWrapper {
    border: none;
}


.playControls__elements {
    justify-content: space-around;
}

.playControls__volume
{
    z-index: 200;
}


.l-container {
    max-width: 100%;
}


.header__inner {
    display: flex;
    justify-content: space-around;
    flex-direction: row;
}

.playControls__wrapper {
    min-width: 0 !important;
    max-width: 100% !important;
    width: 100% !important;
    margin: 0;
}

.playControls__control
{
    height: unset;
}

.playControls__inner
{
    height: 100%;
    padding: 0px;
}

.playControls__elements {
    margin: auto;
    max-width: 1225px;
    width: 100%;
}

.backgroundGradient 
{
    filter: brightness(.9);
}

.userInfoBar
{
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap-reverse;
    padding: 10px;
}

.userInfoBar>*
{
    position: unset;
    bottom: unset;
    top: unset;
    right: unset;
    left: unset;
    padding: 5px;
}

@media (max-width: 975px) {
    
    .userInfoBar
    {
        justify-content: space-around;
    }
    
    .playControls {
        visibility: visible;
    }

    .header__navMenu > * {
        max-width: 33.333%;
    }

    .playControls__elements {
        flex-wrap: wrap-reverse;
        min-width: 340px;
        max-width: 550px;
    }
    .playControls__soundBadge > * {
        width:100%;
    }
    .playControls__soundBadge {
        width: 100%;
    }

    .l-container.l-content {
        box-sizing: border-box;
        padding: 50px 10px;
        overflow: hidden;
        width: 100%;
    }
    
    
    .playControls__queue 
    {
        bottom: 100px;
        max-height: 75vh;
    }
    

    .l-main,
    .l-about-main
    {
        margin-right: 0 !important;
        margin-left: 0 !important;
        
        transition: margin 0.1s
    }

    #content>div>.l-fluid-fixed:hover>.l-main:not(:hover),
    #content>div>.l-listen-wrapper:hover>.l-about-main:not(:hover)
    {
        margin-right: 310px !important;
        margin-left: -310px !important;
    }
    
    .l-sidebar-right {
        right: -320px !important;
        padding: 0 10px;
        border-left: solid 1px currentColor !important;
        
        transition: right 0.1s
    }
    
    .l-sidebar-right:hover {
        right: -10px !important;
    }
    
    
    .listenEngagement
    {
        padding: 10px !important;
        width: 100% !important;
    }
    
    .l-about-rows
    {
        padding: 0 !important;
    }
    
    .l-about-row
    {
        display: flex;
        flex-wrap: wrap;
    }
    
    .l-about-row>*
    {
        display: flex;
        justify-content: space-around;
        position: relative !important;
        padding: 0 !important;
        width: 100% !important; 
    }
    
    .backgroundGradient 
    {
        position: absolute;
        width: 100%;
        z-index: 0;
        top: 0;
        bottom: 0;
    }
    

    .fullListenHero {
        width: 100%;
        margin: 0;
        height: auto;
    }

    .fullHero__foreground {
        width: 100%;
        padding: 0 20px;
        padding-top: 50px;
        position: unset;
        display: flex;
        align-items: center;
        flex-direction: column;
    }

    .fullHero__foreground > * {
        width: auto;
        max-width: 100%;
        height: auto;
        position: unset;
        padding: 5px 0;
        text-align: center;
    }

    .fullListenHero .soundTitle__playButtonHero {
        display:none;
    }

    .fullHero__playerArea {
        width: 100%;
    }

    .fullHero__artwork {
        width: 250px;
        height: 250px;
    }

    .header__inner {
        flex-direction: column;
    }
    header {
        overflow: hidden;
        height: unset !important;
        max-height: 46px;
        transition: all .5s!important;
    }
    header:hover {
        max-height: 200px;
    }
    .header__inner > *,
    .header__inner > * > * {
        display: flex !important;
        justify-content: center;
    }
}
