css:
@-moz-document domain("www.curseforge.com") {
.text-sm {
    font-size: 1.4rem;
    vertical-align: bottom;
}
label.text-sm.text-gray-500 {
    margin-bottom: 5px;
}
.mr-2.text-xs.text-gray-500 {
    font-size: 1.1rem;
    margin-top: -5px;
}
span.text-gray-500 {
    font-size: 1.4rem;
    vertical-align: bottom;
    margin-top: -3px;
}
span.p-comment-postdate.post-date.ml-auto.text-gray-500.mr-4 {
    margin-top: 0;
}
span.text-xs.text-gray-500 {
    font-size: 1.1rem;
    margin-top: -5px;
}
.text-sm.leading-snug {
    font-size: 1.2rem;
}
.text-lg {
    font-size: 2rem;
    padding-right: 6px;
    padding-bottom: 5px;
}
.text-3xl {
    font-size: 4rem;
}
.text-xl {
    padding-left: 5px;
}
div.flex.flex-col.mb-4 {
    margin-bottom: 0;
}
a {
    font-size: 1.4rem;
}
h2.text-xl.font-bold {
    padding-bottom: 5px;
}
.b-list-label {
    font-size: 1.4rem;
}
div.flex.flex-col.h-full.justify-between {
    font-size: 1.4rem;
}
a.flex.flex-col.mt-auto.mb-auto {
    font-size: 1.4rem;
}
svg.icon.icon-offsite-nav {
    margin-left: .4rem;
}
div.w-full.flex.justify-between {
    font-size: 1.4rem;
}
.forum-post-body * {
    font-size: 1.5rem;
}
span.user.user-role-registered-user {
    font-size: 1.7rem;
}
.bg-primary-0 {
    background-color: #eee1f5;
}
.project-detail__content p, .project-detail__content span, .project-detail__content a, .project-detail__content article, .project-detail__content div, .project-detail__content section, .project-detail__content li, .project-detail__content ul {
    font-size: 1.6rem;
}
}
