css:
@-moz-document domain(workona.com) {
	body {
		background-color: #D6E1EC;
		background: linear-gradient(180deg,#E7EEF5,#D6E1EC);
	}

    .c00325{
        background: none;
    }
  
	.c00254,
 	.c00254:hover, /* search field */
  	.c00418,
  	.c00443 {
		background: white;
	}
  	.c00418,
  	.c00331 {
      opacity: 1;
    }
	.c00419 {
		box-shadow: 0 4px 0 white;
	}
  
  	/* tab boxes */
  	.c00444 {
      background: #E7EEF5;
    }
	.c00239 {
		box-shadow: 0 0 0 1px rgba(39,47,59,0.1), 0 1px 1px 0 rgba(39,47,59,0.1);
	}

    .c00324,
    .c00301,
    .c00252,
  	.c0065,
  	.c00189,
  	.c0065:hover,
  	.c00189:hover {
        color: inherit;
    }
  
  	.c00328 { /* Logo */
		filter: invert(1);
    }
  
  	/* ------ optional ------ */
  	/* hide logo */ .c00328 { opacity: 0 }
	/* hide workspace bar */ .c00324 { display: none } .c00260 { left: 0 }
	/* hide search field */ .c00250 { display: none }
    /* center the tabs overview */ .c00430, .c00443 { width: 95%; margin: 0 auto }
}
