css:
@-moz-document url-prefix("https://www.twitch.tv") {
/* Fond */
.tw-root--theme-dark body {
    color: #ff00ff;
    background-color: #2e2d2d;
}
body {   
    color: #b7c2b2;
    background-color: #016b49;
}


/* Barre du haut */
.tw-root--theme-dark .top-nav__menu {
    background-color: #000000;
}
.top-nav__menu {
    background-color: #bb6d61;
}


/* Liste gauche fond */
.tw-root--theme-dark .side-nav__overlay-wrapper, .tw-root--theme-dark .side-nav__theme-wrapper {
    background-color: #050505;
}


/* Liste gauche éléments */
.tw-root--theme-dark .side-nav-card__link {
    background: #050505;
}


/* Barre streamer haut */
.tw-root--theme-dark .channel-header {
    background-color: #2e2d2d;
    border-bottom: 1px solid #000000;
}
.channel-header {
    background-color: #2e2d2d;
    border-bottom: 1px solid #000000;
}


/* Bas lecteur */
.tw-root--theme-dark .channel-info-bar {
    background-color: #050505;
}
.channel-info-bar {
    background-color: #848173;
}

/* Icon follow et cloche fond */
.tw-root--theme-dark .follow-btn--following-icon-btn {
    background-color: #000000;
}

/* S'abonner/offrir un sub */
.tw-core-button--primary, .tw-root--theme-dark .tw-core-button--primary, .tw-root--theme-light .tw-core-button--primary {
    background-color: #000000;
    border-color: transparent;
    color: #ff00ff;
}

/* Barre twitch prime */
.blue-bar, .tw-root--theme-dark .blue-bar, .tw-root--theme-light .blue-bar {
    background: #4502b0;
}


/* S'abonner/offrir un sub - curseur dessus */
.tw-root--hover .tw-core-button--primary:hover, .tw-root--theme-dark.tw-root--hover .tw-core-button--primary:hover, .tw-root--theme-light.tw-root--hover .tw-core-button--primary:hover {
    background-color: #000000;
    border-color: #ff00ff;
    color: #ff00ff;
}


/* Bordure bas */
.tw-root--theme-dark .tw-border-b {
    border-bottom: 1px solid #000000!important;
}
/* Bordure gauche */
.tw-root--theme-dark .tw-border-l {
    border-left: 1px solid #000000!important;
}
/* Bordure droite */
.tw-root--theme-dark .tw-border-r {
    border-right: 1px solid #000000!important;
}
/* Bordure haut */
.tw-root--theme-dark .tw-border-t {
    border-top: 1px solid #000000!important;
}

/* Fond chat haut*/
.tw-root--theme-dark .tw-c-background-alt {
    background-color: #2e2d2d!important;
}

.tw-c-background-alt {
    background-color: #46e0ff!important;
}

/* Fond mini menus */
.tw-root--theme-dark .tw-c-background-base {
    background-color: #1c0c33!important;
}

/*Fond chat*/
.tw-root--theme-dark .chat-room {
    background: #050505;
}
.chat-room {
    background: #8784e3;
}

/*Couleur texte chat*/
.tw-c-text-base {
    color: #1757f6!important;
}

/* Bord chat*/
.tw-border-l {
    border-left: 1px solid #ff0000!important;
}

/* Chat texte box */
.tw-root--theme-dark .tw-textarea {
    background: #2e2d2d;
    border: 1px solid transparent;
    color: #ff00ff;
}
.tw-root--theme-dark .tw-textarea:focus, .tw-root--theme-light .tw-textarea:focus, .tw-textarea:focus {
    border-color: #ff00ff;
    box-shadow: 0 0 6px -2px #ff00ff;
}
.tw-textarea {
    background: #000000;
    border: 1px solid #ff0000;
    color: #454545;
}

/* Couleur lien */
.tw-root--theme-dark .tw-link {
    color: #eb6c28;
}
.tw-root--theme-dark.tw-root--hover .tw-link:hover {
    color: #ed9d72;
}
.tw-root--theme-dark .tw-link:visited {
    color: #eb6c28;
}



/* Curceur sur carré */
.tw-root--theme-dark a, .tw-root--theme-dark a:hover {
    color: #ed9d72;
}


/* Barre de recherche */
.tw-root--theme-dark .tw-input {
    background: #050505;
    border: 1px solid transparent;
    color: #ff00ff;
}
.tw-input:focus, .tw-root--theme-dark .tw-input:focus, .tw-root--theme-light .tw-input:focus {
    border-color: #ff00ff;
    box-shadow: 0 0 6px -2px #ff00ff;
}
}
