css:
@-moz-document url-prefix("https://rozetka"),domain("rozetka.com.ua"){
body{
    background: #1e1d1d;
    color: #fff;
}
a.visited:link, a:visited {
  color: #897e7f;
}
a:link {
  color: #5bb751;
}
.wrap, div.g-i-tile-i.available {
  background-color: #242323 !important
}
.g-compare-icon, .g-wishlist-icon, .g-buy-submit-light {
  background-color: #131519 !important;
  box-shadow: 2px 3px 2px 3px #2844a9 !important;
}
.detail-tabs-i-title-inner {
  color: #b30038;
}
.whitelink:link, .whitelink:visited {
    color: #fff;
}
.header-user-pre-title {
  color: #0051a2;
}
.filter-parametrs-i-l-i-checkbox-disabled {
  color: #ff6565 !important;
}
.filter-parametrs-i-l-i-text {
  color: #0d9000;
}
.main-promotions {
  background: #cccccc;
}

.h1, .h2, .h3, h1, h2, h3{
	color: #fff;
}
.cust-content-inner {
    background: #1a1717;
}
}
