css:
/** COMPATIBLE WITH "CS BUSINESS" BOARD STYLE **/
@-moz-document domain("chickensmoothie.com"){
:root {
    --bg1: #121217;
    --bg2: #383838;
    --bg3: #4f4e4e;
    --bg4: #626161;
    --textcolor1: #dbdacc;
    --textcolor2: #cc4a82;
}

#body_inner{
    background-color: var(--bg1) !important;
    background-image: none !important;
    border: none !important;
}

#navigation a{
    background: none !important;
}

* {
    color: var(--textcolor1) !important;
}

a, h1, h2, h3 {
    color: var(--textcolor2) !important;
}

.panel.bg2, .panel.bg3, #tabs .activetab a, #tabs a span, #tabs a, .panel, .navbar, li > ul, .sf-with-ul, .forumbg, li.row.bg1, li.row.bg2, .liner, .rules, #csbody {
    background-color: var(--bg2) !important;
    background-image: none !important;
    border: none !important;
}

.post.bg2, .post.bg1, th, td, .kind a, textarea, select, input, .button1, .button2, .date, #newssub {
    background-color: var(--bg3) !important;
    background-image: none !important;
    border: none !important;
}

blockquote {
    background-color: var(--bg4) !important;
    border: none !important;
}
#main-menu > li > a > img {
    background-image: none !important;
}

.postlink:hover {
    text-decoration: none !important;
    background-color: transparent !important;
}

.postlink {
    border-bottom-color: transparent !important;
    text-decoration: underline !important;
}

ul.topiclist dd {
    border: none !important;
}

.signature {
    display: none !important;
}

img {
  filter: brightness(70%);
}

.pollbar1, .pollbar2 {
    background-color: #cc4a82 !important;
}

input, textarea {
    border-radius: 3px !important;
}
}
