css:
@-moz-document domain("gw2efficiency.com") {
* {
  font-family: sans-serif !important;
  color: #e5f6ff !important;
  margin: 0px;
  padding: 0px;
}

*:focus {
  outline: none;
}

html,
body,
.eff-html,
.pagewrapper {
  background-color: #101820;
  color: #bfcad0;
  margin: 0;
  padding: 0;
}

img.logo__image {
  filter: grayscale(0%) hue-rotate(200deg) !important;
}

h2 span {
  color: #e8f4ff !important;
}

li::before {
  background-color: initial !important;
  color: #afeaff !important;
}

.item-selection-wrapper .selection {
  background-color: #07090d;
  color: #e8f4ff;
}

.eff-Table tbody td {
  padding: 0px 0.5rem;
}

th div,
.eff-Table tbody td div {
  padding: 0px 1px;
  justify-content: flex-end;
}

input,
input.eff-Input,
input[type="checkbox"],
select {
  padding: 4px !important;
  border-radius: 3px;
  border: 1px solid #243a53 !important;
  background-color: #07090d !important;
}

.eff-Input {
  font-family: initial !important;
}

/* Lists */
.crafting-steps li {
  margin-bottom: 0px !important;
}

.crafting-steps li:nth-child(odd) {
  border: none;
  font-size: initial !important;
  background-color: #0f1a24;
}

.crafting-steps li:nth-child(even) {
  border: none;
  font-size: initial !important;
  background-color: #0f161e;
}

.shopping-list li:nth-child(odd) {
  border: none;
  font-size: initial !important;
}

.shopping-list li:nth-child(even) {
  border: none;
  font-size: initial !important;
}

.eff-Table tr:nth-child(odd) {
  border: none;
  font-size: initial !important;
  background-color: #131c26;
}

.eff-Table tr:nth-child(even) {
  border: none;
  font-size: initial !important;
  background-color: #0f161e;
}

table.c-table tr:nth-child(odd) {
  border: none;
  font-size: initial !important;
  background-color: #131c26;
}

table.c-table tr:nth-child(even) {
  border: none;
  font-size: initial !important;
  background-color: #0f161e;
}

.price,
.single-price {
  border-radius: 2px;
  padding-bottom: 4px !important;
  text-align: right;
  vertical-align: center !important;
  background-color: #121e2b !important;
  border: 1px solid #030609 !important;
}

.single-price {
  border: 1px solid #000 !important;
  background-color: #0a0d11 !important;
}

.price.selected {
  border: 1px solid #306f43 !important;
  background-color: #182f23 !important;
}

input.price.selected {
  color: #243 !important;
}

.eff-background-color-grey-05 {
  background-color: #1c2410;
}

.quicklinks .quicklink-box .quicklink-box__inner {
  border: none;
  border-radius: 5px;
}

.eff-Pagination {
  padding-bottom: 1.5rem;
}

.rarity-1,
.rarity-1 * {
  color: #FFF;
}

.navigation li a {
  color: #FFFFFF;
}

.navigation li.active > a span,
.navigation .second-level li.active span {
  text-shadow: none;
  padding: 4px;
  border-bottom: 2px solid #2bc3ff;
  color: #49dcff !important;
}

.navigation li.active > a > span {
  text-shadow: none;
  font-weight: bold;
  font-size: 1.2rem;
}

.user-container__show-on-desktop a span {
  text-shadow: none;
}

.component-tree .display .prices .price.selected {
  background-color: rgb(30, 30, 30);
}

.component-tree .display .prices .price {
  background-color: #222;
}

th,
th {
  text-align: right !important;
}
}
