css:
@-moz-document regexp("https:\\/\\/www.imooc.com\\/read\\/\\d+\\/article\\/\\d+.*"),
regexp("http:\\/\\/www.imooc.com\\/read\\/\\d+\\/article\\/\\d+.*") {
	.main-con .right-content .container {
		width: 1000px;
	}
	.main-con .right-content .container .center_con {
		width: 1000px;
	}
	.container .left_con {
		left: 44%;
	}

	.main-con .right-content .container .right_con {
		right: 44%;
	}


	/* 代码块 */
	h1,
	h2,
	h3,
	h4 {
		border-bottom: solid 1px #d1d1d1a3;
	}

	.main-con .right-content .container .center_con .article-con .art-content pre > code {
		border: 0;
		background-color: rgba(219, 219, 219, 0) !important;
	}

	/* 代码 */
	code.prism {
		font-family: Monaco, Consolas, 'Andale Mono', 'DejaVu Sans Mono', Microsoft JhengHei, Microsoft YaHei, monospace !important;
	}
	.container .center_con .article-con .art-content code,
	.container .center_con .article-con .art-content kbd,
	.container .cl-preview-section code,
	.container .center_con .article-con .art-content pre,
	.container .center_con .article-con .art-content samp {
		font-family: Monaco, Microsoft JhengHei, Microsoft YaHei, Consolas, "Courier New", monospace !important;
	}

	/* 正文 */
	#article_content,
	.cl-preview-section ul,
	.cl-preview-section ol,
	.cl-preview-section p,
	.cl-preview-section pre,
	.cl-preview-section code,
	#code-copy {
		font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Helvetica, Arial, sans-serif, Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol !important;
	}
}
