@-moz-document url-prefix("https://www.colaboraread.com.br/"), url-prefix("https://colaboraread.com.br/") {
/* Cursos */

body, .pda .menuColaborar {
  background: #222 !important;
  color: #ddd !important;
}

.pda nav.navbar-collapse, a.logoPrincipal.navbar-brand.navbar-brand-pda.hidden-xs {
  background: #333 !important;
}

.pda .logoTitulo, .pda nav.navbar-collapse a, a, .pda .page-header h2, h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
  color: #ddd !important;
}

.panel, .pda .block {
  background: #333 !important;
  border-color: #555 !important;
}

.pda .curso {
  color: #4be !important;
}

.dropdown-menu, .popover, .popover-title {
  background: #444 !important;
}
.popover.bottom > .arrow:after {
  border-bottom-color: #444 !important;
}
.popover-title {
  border-bottom-color: #555 !important;
}
}

@-moz-document url-prefix("https://www.colaboraread.com.br/mensagem/"), url-prefix("https://colaboraread.com.br/mensagem/") {
/* Caixa de entrada */

.nav > li > a:hover, .nav > li > a:focus, .list-group-item:not(.active) {
  background: #444 !important;
}

.list-group-item:not(.active) {
  border-color: #555 !important;
}

.btn-success {
  background: #383 !important;
  border-color: #494 !important;
  color: #ddd !important;
}

button.btn.btn-default.btn-block.mfp-iframe.mb-30 {
  background: #444 !important;
  border-color: #555 !important;
  color: #ddd !important;
}

.btn-default {
  background: #333 !important;
  border-color: #555 !important;
  color: #ddd !important;
}

.panel-default .panel-heading {
  background: #333 !important;
  color: #ddd !important;
  border-right: 1px solid #555 !important;
}

.table-striped > tbody > tr:nth-child(odd) > td, .table-striped > tbody > tr:nth-child(odd) > th {
  background: #373737 !important;
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
  border-color: #555 !important;
}

.form-control {
  background: #111 !important;
  border-color: #555 !important;
  color: #ddd !important;
}

.hr-black-20 {
  border-color: #555 !important;
}

.nav > li > a:hover, .nav > li > a:focus, .list-group-item:not(.active) {
  border-bottom: none !important;
  border-top: 1px solid #555 !important;
}

.list-group-item:last-child:not(.active) {
  border-bottom: 1px solid #555 !important;
}
}

@-moz-document url-prefix("https://www.colaboraread.com.br/aluno/dashboard"), url-prefix("https://colaboraread.com.br/aluno/dashboard") {
/* Dashboard */

.form-control {
  background: #222 !important;
  color: #ddd !important;
  border-color: #555 !important;
}

.pda .btnMaisDetalhes, .pda .btnMaisDetalhes:after {
  background: #222 !important;
  color: #ddd !important;
}

.pda .setaDropdown.collapsed:after, .pda .setaDropdown:after {
  color: #ddd !important;
}

.pda .headDisciplinas, .pda .atividadesCronograma table td, .list-line > li, .pda .opcoes div, .pda .opcoes a, .pda .opcoes span.dadosTutor {
  border-color: #555 !important;
  color: #ddd !important;
}

.pda .chamadaAtencao {
  background: #07f !important;
  color: #fff !important;
}

.text-info {
  font-size: 30;
  color: #3af !important;
}
}

@-moz-document url-prefix("https://www.colaboraread.com.br/aluno/timeline/"), url-prefix("https://colaboraread.com.br/aluno/timeline/") {
/* Timeline */

.btn.btn-default.pull-right.js-bootstrap-tooltip.js-feedback:focus, .btn.btn-default.pull-right.js-bootstrap-tooltip.js-feedback:hover {
  color: #fff !important;
  border-color: #777 !important;
}

.panel-default .panel-heading {
  background: #333 !important;
}

.panel-default > .panel-heading {
  color: #ddd !important;
}

legend, .btn-default, .table-bordered, .table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td {
  border-color: #555 !important;
}

.btn-default {
  background: #444 !important;
}

.table-calendar > thead > tr > th {
  background: #444 !important;
}

.table > thead > tr > td.info, .table > tbody > tr > td.info, .table > tfoot > tr > td.info, .table > thead > tr > th.info, .table > tbody > tr > th.info, .table > tfoot > tr > th.info, .table > thead > tr.info > td, .table > tbody > tr.info > td, .table > tfoot > tr.info > td, .table > thead > tr.info > th, .table > tbody > tr.info > th, .table > tfoot > tr.info > th {
  background: #444 !important;
}

.table-calendar tbody > tr td:hover, .table-calendar tbody > tr td:hover {
  background: #666 !important;
}

.well {
  background: #444 !important;
  border-color: #555 !important;
}

rect.highcharts-background {
  fill: #333 !important;
}

text.highcharts-title {
  fill: #ddd !important;
}

text.highcharts-subtitle {
  fill: #bbb !important;
}

span.text-muted, div > span {
  color: #fff !important;
}

.timeline > li > .timeline-panel {
  background: #333 !important;
  border-color: #555 !important;
}

hr {
  border-color: #555 !important;
}

.btn-default:hover, .step:hover, .btn-default:focus, .step:focus, .btn-default:active, .step:active, .btn-default.active, .step.active, .open .dropdown-toggle.btn-default, .open .dropdown-toggle.step {
  color: #fff !important;
}

.btn-default {
  color: #ddd !important;
}

.timeline > li > .timeline-panel:before {
  border-left: 15px solid #555;
  border-right: 0 solid #555;
}

.timeline > li > .timeline-panel:after {
  border-left: 15px solid #333;
  border-right: 0 solid #333;
}

.timeline:after {
  border-color: #555 !important;
  background: #333 !important;
}

.timeline > li > .timeline-badge:not(.danger) {
  border-color: #555 !important;
  background: #333 !important;
}

.timeline-badge.danger {
  border-color: #000 !important;
  background: #d22 !important;
}

.timeline:before {
  background: #555 !important;
}

.progress {
  background: #666 !important;
}

.progress-bar > small {
  color: #000 !important;
  padding-left: 5px !important;
}

/* Vídeo */

.player-playlist a, div.jp-interface {
  background: #444 !important;
  border-color: #555 !important;
  color: #ddd !important;
}

.jp-play, .jp-pause {
  border-color: #666 !important;
}

.jp-play:hover, .jp-pause:hover {
  border-color: #777 !important;
}

.alert-info {
  background: #379 !important;
  border-color: #59b !important;
  color: #def !important;
}
}

@-moz-document url-prefix("https://www.colaboraread.com.br/aluno/avaliacao/"), url-prefix("https://colaboraread.com.br/aluno/avaliacao/") {
/* Avaliação */

.panel-default .panel-heading {
  background: #333 !important;
}

.panel-default > .panel-heading {
  color: #ddd !important;
}

hr {
  border-color: #555 !important;
}

.btn-default:hover, .btn-default {
  background: #444 !important;
  border-color: #555 !important;
}

.alert-success, .alert-warning {
  background: #474 !important;
  border-color: #585 !important;
  color: #ada !important;
}

.alert-dismissable .close {
  color: #ada !important;
  opacity: 1 !important;
}

.alert-dismissable .close:focus, .alert-dismissable .close:hover {
  color: #000 !important;
  opacity: 1 !important;
}

button.btn.btn-default.btn-block.mb-10 {
  color: #ddd !important;
}

button.btn.btn-default.btn-block.mb-10:hover, button.btn.btn-default.btn-block.mb-10:focus {
  color: #fff !important;
  border-color: #777 !important;
}

.mfp-content {
  background: #333 !important;
}

.modal-header, .modal-footer {
  border-color: #555 !important;
}

.btn-default:hover {
  color: #fff !important;
  border-color: #777 !important;
}
}

@-moz-document url-prefix("https://www.colaboraread.com.br/aluno/forum/"), url-prefix("https://colaboraread.com.br/aluno/forum/") {
/* Fórum */

.panel-default .panel-heading, .panel-footer {
  background: #333 !important;
}

.list-line > li, .panel-footer, hr {
  border-color: #555 !important;
}

.panel-title, .display-block {
  color: #ddd !important;
}

.btn-group * {
  background: #444 !important;
  border-color: #555 !Important;
  color: #ddd !important;
}

.btn-group:hover *, .btn-group:focus * {
  border-color: #666 !Important;
  color: #fff !important;
}

.forum-item-content:after {
  border-right-color: #333 !important;
}

.forum-item-content:before {
  border-right-color: #555 !important;
}
}

@-moz-document url-prefix("https://www.colaboraread.com.br/biblioteca/digital/"), url-prefix("https://colaboraread.com.br/biblioteca/digital/") {
