css:
@-moz-document domain("playerduo.com")
{
  #root,#root .text-donate,#root .wrapper,body
  {
    background-color: #252424;
  }

  body
  {
    color: #01f7ed;
  }

  .Navbar
  {
    background-color: #252424;
  }

  .Navbar__Link-menu a
  {
    color: #ffffff;
  }

  .section-title
  {
    color: #ffef00;
    font-weight: bold;
  }

  .card-item
  {
    border-color: #48ffc7;
  }

  .dropdown-menu-wrap .user-info .info p:first-child
  {
    color: #ffef00;
    background-color: #252424;
  }

  .dropdown-menu
  {
    background-color: #252424ed;
    box-shadow: 0px 3px 5px 1px #3bad8a;
  }

  .dropdown-menu-wrap.nav .open>a, .dropdown-menu-wrap.nav .open>a:focus, .dropdown-menu-wrap.nav .open>a:hover
  {
    background-color: #252424;
  }

  .dropdown-menu-wrap:hover
  {
    box-shadow: inset 1px 2px 30px 1px #f40000;
  }

  .dropdown-menu-wrap .content .main .menu a
  {
    color: #3bad8a;
  }

  .notification-item p
  {
    color: #ff53b1;
  }

  .notification-item .msg .detail p strong
  {
    color: #ffffff;
    text-shadow: 0 0 3px #11da17;
  }

  li.notification-item
  {
    padding: 8px 16px;
    border-bottom: 1px solid #0d53e0;
  }

  li.notification-item:hover
  {
    background-color: #000000;
    box-shadow: inset 0px 0px 18px 1px #27ff00;
  }

  .ui-category .title-category span
  {
    background-color: #252424;
  }

  .player-profile-main-wrap .name-player-profile
  {
    color: #ffffff;
    text-shadow: 0 0 4px rgba(255,255,255,1) , 0 0 7px rgba(255,255,255,1) , 0 0 1px rgba(255,255,255,1) , 0 0 12px #ff00de , 0 0 20px #ff00de , 0 0 30px #ff00de , 0 0 40px #ff00de;
  }

  .nav-player-profile .item-nav-name
  {
    color: #ffffff;
  }

  .player-profile-left-wrap .member-since span:last-child
  {
    color: #ffffff;
  }

  .table-achievement tr td:first-child
  {
    color: rgb(175,123,0);
  }

  .player-profile-main-wrap .content-player-profile p
  {
    color: white;
  }

  .player-profile-main-wrap a
  {
    color: #f0564a;
  }

  .player-profile-main-wrap a:hover
  {
    color: rgb(106,158,255);
  }
/*Text*/
  .player-profile-main-wrap #top-donate ul.nav-tabs li a
  {
    color: white;
  }

  .nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover
  {
    background-color: #ff434326;
    box-shadow: inset 0 0 7px 1px #fbff00;
  }

  .mg-24 .top-donate-player .total-amount
  {
    color: white;
  }

  .mg-24 .top-donate-player .ky-1
  {
    color: white;
  }

  .title-player-profile span
  {
    color: white;
  }

  .content-player-review
  {
    color: white;
  }

  .review-duo-player .full-size .wrapper-content-rating .review-rating .time-rent-review
  {
    color: #ffd375;
  }

  .notification-wrap.nav .open>a, .notification-wrap.nav .open>a:focus
  {
    background-color: #252424;
  }

/* scrollbar */
  ::-webkit-scrollbar
  {
    width: 10px;
  }

/* Track */
  ::-webkit-scrollbar-track
  {
    background: #f1f1f1;
  }
 
/* Handle */
  ::-webkit-scrollbar-thumb
  {
    background: #888;
  }

/* Handle on hover */
  ::-webkit-scrollbar-thumb:hover
  {
    background: #555;
  }
/* end */

  .wrapper .chat-main-wrap .main-header .name
  {
    color: #252424;
    text-shadow: 0 0 4px rgba(118,123,255,0.63) , 0 0 7px rgba(118,123,255,0.63) , 0 0 1px rgba(118,123,255,0.63) , 0 0 12px #4b7dff , 0 0 20px #4b7dff , 0 0 30px #4b7dff , 0 0 40px #4b7dff;
  }

  .notification-item .msg .detail p:nth-child(2)
  {
    color: wheat !important;
  }

  .msg-wrap .msg-body .msg a
  {
    color: #ff6cf7;
  }

  .list-message div > span
  {
    color: #ffffff;
    font-weight: bold;
    text-shadow: 0 0 3px #e4ff03;
  }

  .msg-card-wrap .info .name
  {
    color: rgb(255,121,234);
  }

  .modal-content
  {
    background-color: #252424d1;
    border: 1px solid rgb(255, 0, 0);
  }

  .rank-modal-tab ul.nav-tabs li a, .rank-modal-tab ul.nav-tabs li a:active, .rank-modal-tab ul.nav-tabs li a:focus, .rank-modal-tab ul.nav-tabs li a:hover
  {
    color: #f5ff11;
  }

  .rank-modal-tab ul.rank-list li img, .rank-modal-tab ul.rank-list li p
  {
    color: #fff;
  }

  .modal-content .modal-header h4
  {
    color: #fff307;
    text-shadow: 4px 0px 5px #e60606;
    text-shadow: 3px 0px 5px #e60606;
    text-shadow: 5px 0px 5px #e60606;
  }

  .fieldGroup p
  {
    color: white;
  }

  .customer-select p
  {
    color: white;
  }

  .table-hover > tbody > tr:hover
  {
    background-color: #ff0579ab;
  }

  .table-hover a
  {
    color: #67e2f3;
  }

  .history-donate-table tr td, .history-duo-table tr td
  {
    color: #efa1ff;
  }

  .table-bordered .table-withdraw-th
  {
    color: #fffe00;
  }

  .player-setting-duo .table-duo-setting tr td p
  {
    color: white;
  }

  .player-history-donate .table-donate tr td, .player-history-duo .table-duo tr td
  {
    color: #efa1ff;
  }
}
