css:
@-moz-document domain("crunchyroll.com") {
/*! Crunchyroll Dark - XHS v1.1.2 (2019-05-20)
/*! Repository: https://github.com/Soitora/xhs-styles/
/*! License:	https://creativecommons.org/licenses/by-sa/4.0/
*/

/* Variables */

:root {
	--xhs-primary: #1A1A1D;
	--xhs-secondary: #1E1E21;
	--xhs-border: #17171A;
	--xhs-highlight: #232326;
	--xhs-title: #C8C8C8;
	--xhs-body: #AAAAAA;
	--xhs-orange: #1e1e1e;
	--xhs-blue: #3779A1;
	--xhs-green: #648E76;
	--xhs-purple: #8E78A2;
	--xhs-brown: #7A6A5A;
	--xhs-red: #EF4A4B;
}

/*! Main Code !*/

/* Changes for v1.1.1 */

.resume-play {
    background: #c76200;
}
.add-queue-button.not-queued {
    background: #004868;
}
.availability-notes-low a.text-link {
    color: #F1F1F1;
}
.rating-histogram .rating-proportion {
    background: #c76200;
}
.header-menubar li a:hover, .header-menubar li.state-selected a {
    color: #c76200;
}
.header-menubar .freetrial-note, .add-queue-button.not-queued {
    color: #c76200!important;
}
.cart-badge, .user-notification-badge {
    background: #c76200;
    color: #F1F1F1;
}
#template_body .special-button, #template_body a.special-button, #template_body input.special-button, #template_body span.special-button, .special-button, a.special-button, input.special-button, span.special-button {
    text-shadow: 0 1px 4px #1e1e1e!important;
    border: 0px!important;
    background: #1e1e1e!important;
}
.old-template-body a.block-link, a.block-link, .new-template-body a.block-link {
    color: #0A6DA4!important;
}
select {
    background: #000000;
    color: #BBB;
    border: solid 1px #555;
}
a.bbtool img {
    background-color: #eee;
    border: 1px solid #aaa;
}
.button.medium-button, a.button.medium-button, input.button.medium-button, span.button.medium-button {
    background: #1e1e1e!important;
    color: #F1F1F1!important;
}
.payment-method-label {
    background-color: #000000;
    color: #F1F1F1;
    border: 0px!important;
}
.error-details {
    background-color: #000000;
    color: #F1F1F1;
}

/*
/* Changes for v1.1.0
*/

.availability-notes-high {
	background: #1e1e1e;
}
.availability-notes-low {
	background: #004868;
}
.rating-histogram .rating-bar {
	background: #212121;
}
ul.topbar-nav-sub li.highlight a {
	background-color: #1e1e1e!important;
	color: #fff
}
#template_action_button a,
.action-button {
	border-width: 0px;
	background-color: #1e1e1e;
	color: #fff;
}
#template_action_button a:hover,
.action-button:hover {
	opacity: 0.8;
	border-width: 0px;
	background-color: #1e1e1e;
	color: #fff;
}
.search-submit-button {
	border-right: 0px solid;
	border-top: 0px solid;
	border-bottom: 0px solid;
	background-image: url(https://i.imgur.com/z3rpQvd.png);
}
a.forum_main-topic-desc-numcomments {
	background: url(https://i.imgur.com/jXhwCbT.png) no-repeat;
}
.widget-forumcategory-display-title .widget-forumcategory-display-title-link {
	color: #aaa;
	font-weight: 700;
	flex: 0 1 auto;
}
.showforumtopic-message-user,
.showforumtopic-message-user-footer {
	border-right: 2px solid #242424;
}
.showforumtopic-message-footer {
	border-bottom: 2px solid #242424;
}
.paginator-lite,
.paginator-lite-selected {
	background-color: #000000;
	border: 1px solid #505050;
	color: #f1f1f1;
}
.paginator-lite-disabled {
	border: 1px solid #242424;
	background-color: #000000;
}
.paginator-lite:hover {
	background-color: #0A6DA4;
}
.bb-quoters {
	background: url(https://i.imgur.com/PSggody.png) no-repeat;
}
.bb-quoters-end {
	background: url(https://i.imgur.com/595D40Q.png) no-repeat;
}
.showforumtopic-message-user-thumblink img {
	background-color: #000000;
	border: 0px;
}
.premium-value-props {
	background: #000000;
	color: #ccc;
	fill: #c76200;
}
main>footer {
	background: #000000;
}
.form-area {
	background: #00000000;
	color: #606060;
}
select#sort-selector {
	background: #000000;
	color: #BBB;
	border: solid 1px #555;
}
#welcome_right > div:nth-child(2) > div {
    border: 0px!important;
}
.welcome-recent-ep a {
    display: inline;
    padding: 2px 0;
    color: #ccc;
}
::-webkit-scrollbar {
	width: 9px;
	height: 9px;
}
::-webkit-scrollbar-button {
	width: 0px;
	height: 0px;
}
::-webkit-scrollbar-thumb {
	background: #1e1e1e;
	border: 0px none #ffffff;
	border-radius: 13px;
}
::-webkit-scrollbar-thumb:hover {
	background: #191919;
}
::-webkit-scrollbar-thumb:active {
	background: #191919;
}
::-webkit-scrollbar-track {
	background: #000000;
	border: 0px none #ffffff;
	border-radius: 26px;
}
::-webkit-scrollbar-track:hover {
	background: #000000;
}
::-webkit-scrollbar-track:active {
	background: #000000;
}
::-webkit-scrollbar-corner {
	background: transparent;
}

/* 
/* CSS changes as per GitHub Issue #4 by TBlazeWarriorT
*/

#source_home #main_content .landscape-element {
    background: #303030;
}

#source_home .landscape-grid li .queue-wrapper {
	background: #303030;
}
#source_home .queue-to-top {
	background: url(https://i.imgur.com/kBuiJBq.png) -3px 0 no-repeat;
}
#source_home .dropdown-collection {
	background: url(https://i.imgur.com/PofM8pS.png) 0 0 no-repeat;
}
#source_home .more-options {
	background: #111;
}
.collection-carousel-arrow {
	background: url(https://i.imgur.com/L9EFgy0.png) no-repeat -40px 42px;
}
body,
.new-template-body,
textarea,
ul.news-top-stories-row li a,
.anime-lineup-blurb,
span,
ul.news-abbr .byline {
	/*General text*/
	color: #f1f1f1 !important;
}
.new-template-body h1 {
	/*Ep name*/
	color: #aaa;
}
.portrait-element,
#store_items li {
	background: #252525;
}
.button.medium-button,
a.button.medium-button,
input.button.medium-button,
span.button.medium-button {
	background: #292929;
}
html.no-js body.main-page.responsive.enUS div#template_scroller.ad-skin div#template_container.cf.template-container.no-adskin div#template_body.cf.new-template-body div div#showmedia.cf.new_layout div#main_content.left div.guestbook.comments.box div#boxMEDIAKEY_781148.boxcontents div.guestbook-sort-bar div.sort-options.small-margin-right select#sort-selector {
	background: #333;
	color: #000;
	border: solid 1px #000                                        
}

/*
/* CSS for MAL-Sync
*/

#source_showview .season-dropdown.open, #source_showview .season-dropdown {
    color: #8A8A8A;
    background-color: #000000!important;
    border: 0px!important;
}

/*
/* Main
*/

.container-shadow {
	background-color: #000000;
}
* {
	text-shadow: none!important;
	text-decoration: none !important;
	box-shadow: none!important;
	border-radius: 0px!important;
}
img.portrait,
img,
.thumb-group-small-mug a img,
.user-profile-thin-gb .guestbook-mug img,
.thumb-user-small-mug a img,
.search-video-thumbs .media-thumb-wide,
.welcome-crnews-mug img,
.welcome-news-mug img,
.welcome-media-mug img,
.newsfeed-bottom-nav {
