}
/* Comments */

textarea {
	background: #000000;
	border: 0px solid red;
}
textarea.text-input {
	border: 0px solid red;
}
.comments .more-replies {
	background-color: #000000;
	border-top: 1px solid #000000;
}
.comments .boxtitle {
