	color: #8A8A8A;
}
.header_search_result_type {
	color: #0A6DA4;
}
.header-search-autocomplete a:focus,
.header-search-autocomplete a:hover,
.header_search_autocomplete_item_focused {
	background-color: #2E2E2E;
}
.search-body {
	border-left: 1px solid #2E2E2E;
}
ul.search-tabs .highlight a,
ul.search-tabs a:hover {
	background-color: #000000;
}
ul.search-tabs a {
	color: #0A6DA4;
}
.search-video-thumbs .series,
.search-results .name {
	color: #0A6DA4;
}
.search-video-thumbs .name {
	color: #0A6DA4;
}
/* Forms */

.forms {
	background: #000000;
	border: .1rem solid #2E2E2E;
}
.std-form input,
.std-form textarea {
	background: #000000;
}
.std-form input,
.std-form select,
.std-form textarea {
	border: .1em solid #2E2E2E;
	border-top-color: #2E2E2E;
	border-right-color: #2E2E2E;
	border-bottom-color: #2E2E2E;
	border-left-color: #2E2E2E;
}
input.validated-input:invalid,
textarea.validated-input:invalid {
	background: url(http://www.crunchyroll.com//i/freetrial/validation/icon_x.png) #000000 right 1.3em center/1em no-repeat;
}
.std-form [name="signup_with_profile[gender]"],
.std-form [type="radio"] {
	background: #000000;
	border: .1em solid #2E2E2E;
}
.std-form [type="radio"]:checked {
	background: #F78C25;
	border: .1em solid #2E2E2E;
}
.std-form [type="checkbox"] {
	border: .1em solid #2E2E2E;
}
.signup-form-section {
	border-right: .0625rem solid #2E2E2E;
}
.login-page .form-area,
.resetpw-page .form-area {
	background-color: #000000;
}
/* Message Box*/

.message-container {
	background: #293940;
}
.search-form-channel input[type=text] {
	border: 0px solid #A8A8A8;
	border-left: 2px solid #8A8A8A;
}
/* Body */

body.main-page {
	background-color: #000000 !important;
}
body {
	background-color: #000000 !important;
}
body,
.new-template-body,
textarea,
ul.news-top-stories-row li a,
.anime-lineup-blurb,
span,
ul.news-abbr .byline {
	color: #8A8A8A;
}
#template_container.template-container,
#template_body.old-template-body,
.lineup-container,
#main_content .landscape-element,
#source_manga_showview #main_content .landscape-grid li .wrapper {
	background: #000000;
}
/* Content */

.welcome-simulcasts .welcome-simulcasts-collection a {
	color: #A8A8A8;
}
.welcome-simulcasts .welcome-simulcasts-episode:hover {
	background-color: #000000;
}
.welcome-simulcasts .welcome-simulcasts-episode {
	color: #A8A8A8;
}
.show-all-simulcasts-button:hover {
	border-color: #000000;
}
.show-all-simulcasts-button {
	background: #000000;
	border: 1px solid #000000;
	color: #8A8A8A;
}
/* Welcome News*/

.welcome-crnews-title,
.welcome-news-title {
	color: #0A6DA4;
}
/* Free Trial */

#showmedia_free_trial_signup {
	background: #000000;
	border: 0px solid #2E2E2E !important;
	border-left: 1px solid #2E2E2E !important;
	border-right: 1px solid #2E2E2E !important;
	border-bottom: 1px solid #2E2E2E !important;
}
.showmedia-free-trial-header {
	background: #000000;
	border: 0px solid #2E2E2E !important;
	border-top: 1px solid #2E2E2E !important;
}
