}
.welcome-countdown-items a {
	color: #14A9FF;
}
.welcome-countdown-items a:hover {
	background-color: #2E2E2E;
}
.welcome-recent-link a {
	color: #A8A8A8;
}
/* Footer */

#footer_menu {
	background-color: #000000;
}
#footer_menu {
	display: none;
}
.isolated-page-footer {
	background-color: #000000;
	border-top: .1em solid #2E2E2E;
}
