}
.portrait-element,
#store_items li {
	background: #000000;
}
.wrapper .portrait-element:active,
body .wrapper .landscape-element:active {
	background: #000000!important;
}
.new-template-body h3,
.guestbook-list,
ul.news .body,
.news-footer-more,
.search-results a,
#source_manga_showview .more-options,
ul.newsfeed .body {
