css:
@-moz-document domain("sketchfab.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

body.white-background,

.c-popup,

.two-columns.model-container,

.c-search-overlay,

.popup-container.fullscreen,

body.blue-background
{
  background: #3d3d3f;
}

.home__section, .home__section,
.home .home-features,

.collection-smallcard, .model-smallcard,

.placeholder__card,

.c-search-header,

.model-container .store-informations__header,
.model-container .store-informations__model,

.header-content, .header-menu .elements .item,

.c-upload__drop,

.c-card-slider__background,

.two-columns.sidebar-right .right .sidebar-box,

.sidebar .link:hover,

.settings>.inner,

.c-model-properties .c-side-settings,

.c-markdown-editor__header,

.model-container
{
    background: #363639;
}

input, select, textarea,
  
.c-page-header,

.card, .card-thumbnail,

.categories-navigation,

.hover-menu,

.dropdown-label .item.category-item, .form-list .item.category-item, .form-listbox .item.category-item, .form-menu-list .item.category-item, .item-list .item.category-item, 
.dropdown-label .item, .form-list .item, .form-listbox .item, .form-menu-list .item, .item-list .item,

.c-comment-form .new .c-comment-form__editor-container,

.c-popup__header,

.placeholder__image, .placeholder__text,

.c-checkbox__actor,

.btn-primary-inverted.form-button, .button.btn-primary-inverted,

.c-page-header__action__popin,
.activity,

.c-author-select__dropdown.c-dropdown, .c-project-select__dropdown.c-dropdown,

.disabled.dropdown-label, .disabled.form-listbox, .disabled.form-prefixed-input, .dropdown-label[disabled], .form-input.disabled, .form-input[disabled], .form-listbox[disabled], .form-prefixed-input[disabled], .btn-primary-inverted-border.form-button, .button.btn-primary-inverted-border,
.form-select:after,

.dropdown-label, .form-input, .form-listbox, .form-prefixed-input,

.c-user-menu__avatar .image-container,

.c-model-properties__header,
.c-dropdown .c-dropdown__label,
.c-dropdown__menu, .c-model-properties__categories-dropdown,
.cm-s-neo.CodeMirror
{
    background: #2e2e2f !important;
}

.c-markdown-editor__tab-buttons>button,
.c-model-management-download__tab, .c-model-management-download__tab:hover
{
    background: #2e2e2f;
}

.c-page-header .c-search-bar__box,
.c-page-header .c-search-bar__icon,
.c-page-header .c-search-bar__input,

.activity:hover,
  .header-notifications__see-all,
  
  .item-list.list-static .item, .list-static.dropdown-label .item, .list-static.form-list .item, .list-static.form-listbox .item, .list-static.form-menu-list .item
{
    background: #1c1c1c9c !important;
}

.btn-store.form-button, .button.btn-store
{
  background: #74173a;
}

.c-model-metadata
{
  background: #0000;
}

body, h1, h2, h3, h4, h5, h6, p, input, select, textarea, ::placeholder,

.card__footer__title a.model-name__label,

.categories-navigation__menu,

.c-page-header .c-navigation__item,
.hover-menu, .model-license__name,

.model-container .main .model-informations__stats,

.c-model-metadata__list,
.dropdown-label .item.category-item, .form-list .item.category-item, .form-listbox .item.category-item, .form-menu-list .item.category-item, .item-list .item.category-item,.dropdown-label .item, .form-list .item, .form-listbox .item, .form-menu-list .item, .item-list .item,

.c-popup__title,

.c-search-header__type-item, .c-search-header,

.c-model-3d-information__label, .c-model-3d-information__value,

.activity__title,
.c-user-menu__name,

.c-upload,

.categories-container .website, .profile-summary .website, .tags-container .website,
.sidebar .link,

.settings>.inner .form-label,

.c-model-properties__header,
.c-model-properties__label,
.c-dropdown .c-dropdown__label,
.cm-s-neo.CodeMirror,

.c-model-metadata__stats
{
  color: #ccc !important;
}

.home__title,

.form-error,

.card-model__price, .model-container .store-informations__header__price
{
    color: #ff5b98 !important;
}

.c-model-3d-information__icon
{
  fill: #ccc;
}

.c-page-header__action__icon path
{
  fill: #ccc !important;
}

.c-add-to-cart-icon__cart path
{
  fill: #ff5b98 !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

.c-page-header__action__popin:before, .c-page-header__action__popin:after
{
  display: none !important;
}

.c-upload__image,
.card-thumbnail:before
{
  filter: invert(1) hue-rotate(180deg) saturate(2) brightness(8);
  mix-blend-mode: screen;
}

.c-plan-brands .c-plan-brands__brand img
{
  filter: brightness(1.5);
}
}

@-moz-document domain("sketchfab.com") {
body.page-embed, html
{
  background: #0000
}
}
