css:
@-moz-document domain("www.typescriptlang.org") {
.docs-container .container {
	width: unset;
	display: grid;
	grid-column-gap: 1rem;
	grid-row-gap: 1rem;
	grid-template-columns: auto auto;
	justify-content: start;
}

.docs-container nav.xs-toc {
	padding: unset;
	font-size: 1.8em;
	margin-bottom: 2rem;
}

.docs-container .dropdown-menu {
	top: 28px;
	left: 10px;
}

.docs-container .toc-container {
	grid-column: 1;
	overflow: unset;
	margin-right: unset;
	width: unset;
	padding-left: unset;
	padding-right: unset;
}

.docs-container .doc-content-container {
	grid-column: 2;
	padding: unset !important;
	margin-right: unset !important;
	padding-left: unset !important;
	margin-left: unset !important;
	width: unset;
}

header {
	hight: 4rem;
}

.docs-container .docs-nav {
	margin-top: unset;
	position: relative;
}

.update-banner {
	background-color: white;
	width: unset;
	padding: 0.3em !important;
	min-height: 4em;
	margin: 0.5rem;
}

.docs-container .post-header {
	padding: unset;
}

h1.post-title {
	margin-top: unset;
	margin-bottom: unset;
}

.docs-container .post-content {
	color: #242424;
	padding-left: unset;
	padding-top: unset;
}

.container {
	padding-right: 5px;
	padding-left: 5px;
	margin-right: auto;
	margin-left: auto;
}

}
