css:
@-moz-document url-prefix("https://monopoly-one.com") {
/* FONTS */
@font-face /* regular */
{
    font-family: 'Google Sans';
    src: url('http://github.com/sahibjotsaggu/Google-Sans-Fonts/raw/master/GoogleSans-Regular.ttf/');
    font-weight: normal;
    font-style: normal; }

@font-face /* italic */
{
    font-family: 'Google Sans';
    src: url(https://github.com/sahibjotsaggu/Google-Sans-Fonts/raw/master/GoogleSans-Italic.ttf);
    font-weight: normal;
    font-style: italic; }

@font-face /* bold */
{
    font-family: 'Google Sans';
    src: url(https://github.com/sahibjotsaggu/Google-Sans-Fonts/raw/master/GoogleSans-Bold.ttf);
    font-weight: bold;
    font-style: normal; }

@font-face /* bold italic */
{
    font-family: 'Google Sans';
    src: url(https://github.com/sahibjotsaggu/Google-Sans-Fonts/raw/master/GoogleSans-BoldItalic.ttf);
    font-weight: bold;
    font-style: italic; }

body {
    font-family: 'Google Sans' }

/*
@import url('https://fonts.googleapis.com/css?family=Roboto&display=swap');
body{font-family: 'Roboto';}
