@-moz-document url-prefix("https://monopoly-one.com") {
/* SCROLLBAR */
::-webkit-scrollbar-button {
    height: 0px; }

::-webkit-scrollbar {
    width: 0px;
    height: 0px; }

::-webkit-scrollbar-thumb {
    border-width: 0 0 0 0;
    border-color: #0000;
    background: #0000; }

::-webkit-scrollbar-thumb:vertical {
    border-radius: 0px 0 0 0px; }

::-webkit-scrollbar-thumb:horizontal {
    border-radius: 0px 0px 0 0; }

::-webkit-scrollbar-corner {
    background: #0000 }
}

@-moz-document url("https://monopoly-one.com/") {
/* STARTPAGE */
/*

body {
    display: none }

*/
}

@-moz-document url-prefix("https://monopoly-one.com") {
/*EVERYTHING*/
.footer {
    margin: 0 0 0 0 }

.header-logo {
    pointer-events: none;
    cursor: default; }

.header .header-logo > div {
    font-size: 20px; }

.profile-top-info-avatar._online {
    padding: 4px; }

.grid-of-4 {
    display: none!important }

.profile {
    margin-bottom: 0!important; }

.profile-top-stat-list {
    background: rgba(0, 0, 0, .6);
    padding: 8;
    border-radius: 10px;
    width: min-content;
    height: min-content; }

.profile-top-stat-list-one._friends ._friends-list {
    padding-top: 6px; }

.profile-top-stat-list-one._friends ._friends-link {
    padding: 5px 0 0 10px; }

.profile-top-cover > ._fade {
    background: rgba(0, 0, 0, .2); }

.profile-top-info-main {
    -webkit-box-flex: 0;
    -ms-flex-positive: 0;
    flex-grow: 0;
    margin-left: 25px;
    color: #fafafa!important;
    background: rgba(0, 0, 0, .6);
    padding: 10px;
    border-radius: 10px;
    width: fit-content!important;
    height: unset;
    min-width: 535px; }

.profile-top-info-avatar {
    margin: 0 }

.inventory-helper-body-top {
    border-bottom: 1px solid;
    border-bottom-color: #21212180; }

.block.games-topWeek, .games-market {
    display: none }

.marketBanner {
    display: none }

.games-gchat-messages {
    height: 400px; }

.block.block-notrimcontent.games-friends {
    box-shadow: 0 0 10px #0008; }

.block.games-gchat {
    box-shadow: 0 0 10px #0008; }

.block.block-notrimcontent {
    box-shadow: 0 0 10px #0008;
    margin-bottom: 20px; }

.profile-top-info-main-rank > img {
    border-radius: 10px; }

.thing .thing-image {
    border: none; }

.thing {
    box-shadow: 0 0 7px #0007;
    border-radius: 10px; }

.thing .thing-about {
    border-radius: 0 0 10px 10px; }

.profile-body-level-info-left {
    display: none; }
}

@-moz-document url-prefix("https://monopoly-one.com") {
/* COLORS */
body:not([theme]) {
    background-color: #fafafa }

.button.button-grass {
    background: linear-gradient(30deg, #0D47A1, #2196F3)!important;
    color: #fafafa!important }

.profile-top-info-avatar._online {
    border: 7px solid #1976D2!important;
    box-shadow: 0 0 6px rgba(255, 255, 255, 0.6); }

body:not([theme]) .header-user-menu-one:hover {
    background: linear-gradient(30deg, #0D47A1, #2196F3); }

.button.button-aqua {
    background: linear-gradient(30deg, #0D47A1, #2196F3)!important;
    color: #fafafa!important; }

.profile-top-stat-list {
    color: #fafafa; }

.profile-body-inventory.block {
    background-color: #fafafa!important;
    box-shadow: 0 0 10px #000a; }

.block {
    border-bottom: 0; }

.header[mnpl-style=transparent-white] .header-menu-one {
    color: #212121;
    font-weight: normal; }

.header.block {
    background: linear-gradient( to bottom, hsl(0, 0%, 100%) 0%, hsla(0, 0%, 100%, 0.987) 8.1%, hsla(0, 0%, 100%, 0.951) 15.5%, hsla(0, 0%, 100%, 0.896) 22.5%, hsla(0, 0%, 100%, 0.825) 29%, hsla(0, 0%, 100%, 0.741) 35.3%, hsla(0, 0%, 100%, 0.648) 41.2%, hsla(0, 0%, 100%, 0.55) 47.1%, hsla(0, 0%, 100%, 0.45) 52.9%, hsla(0, 0%, 100%, 0.352) 58.8%, hsla(0, 0%, 100%, 0.259) 64.7%, hsla(0, 0%, 100%, 0.175) 71%, hsla(0, 0%, 100%, 0.104) 77.5%, hsla(0, 0%, 100%, 0.049) 84.5%, hsla(0, 0%, 100%, 0.013) 91.9%, hsla(0, 0%, 100%, 0) 100%) }

body:not([theme]) .block {
    background-color: #fff0; }

body:not([theme]), body:not([theme]) input, body:not([theme]) textarea {
    color: #212121!important; }

.header-logo > svg > path, .header-logo > svg > path, .header-logo > div {
    fill: #212121!important;
    color: #212121!important; }

.header .header-menu .header-menu-one .badge[mnpl-counter=streams] {
    background: linear-gradient(30deg, #0D47A1, #2196F3)!important;
    color: #fafafa; }

.table-body-players {
    color: #fafafa; }

.header-menu-one {
    color: #212121!important }

.header[mnpl-style=transparent-light] .header-right > div:before, .header[mnpl-style=transparent-white] .header-right > div:before, .header-right > div:before, .header-right > div:before {
    color: #212121!important;
    transition: color .2s ease-in-out; }

.header[mnpl-style=transparent-light] .header-right > div:hover:before, .header[mnpl-style=transparent-white] .header-right > div:hover:before {
    color: #1976D2!important }

.thing.thing__quality_1 .thing-about, .thing[mnpl-quality="1"] .thing-about, .item.item-q1 .item-name {
    background: linear-gradient(30deg, #03A9F4, #81D4FA); }

.thing.thing__quality_2 .thing-about, .thing[mnpl-quality="2"] .thing-about, .item.item-q2 .item-name {
    background: linear-gradient(30deg, #1565C0, #2196F3); }

.thing.thing__quality_3 .thing-about, .thing[mnpl-quality="3"] .thing-about, .item.item-q3 .item-name {
    background: linear-gradient(30deg, #9C27B0, #CE93D8); }

.thing.thing__quality_4 .thing-about, .thing[mnpl-quality="4"] .thing-about, .item.item-q4 .item-name {
    background: linear-gradient(30deg, #D81B60, #F06292); }

.thing.thing__quality_5 .thing-about, .thing[mnpl-quality="5"] .thing-about, .item.item-q5 .item-name {
    background: linear-gradient(30deg, #FFA000, #FFCA28); }

.inventory-helper-body.block {
    background-color: #fafafa!important }

.inventory-helper-body-top {
    background-color: #fafafa!important; }

.inventory-helper-body-top-title, .inventory-helper-body-top-data {
    color: #212121!important;
    font-weight: bold; }

.inventory-helper-arrow {
    border-color: #fafafa!important }

.profile-body-level-info {
    text-shadow: 0 0 1px #000; }

.profile-body-level-info-progress > div {
    background: linear-gradient(30deg, #0D47A1, #2196F3)!important;
    box-shadow: 0 0 0 #0000; }

.profile-body-level-info-progress {
    background: #fafafaa0;
    box-shadow: 0 0 4px #000a; }

.profile-body-inventory .title .title-button:hover {
    background: linear-gradient(30deg, #0D47A1, #2196F3)!important;
    color: #fafafa!important; }

.profile-top-info-main-mods ._vip {
    background: linear-gradient(30deg, #FFA000, #FFCA28); }

.profile._nocover ._friends-link:hover {
    color: #fafafa!important; }

.profile._nocover ._friends-link {
    color: #afb0af!important; }

.profile._nocover .profile-top-stat-actions-button {
    border-color: #0000;
    background: linear-gradient(30deg, #0D47A1, #2196F3)!important; }

.button.button-free.button-small {
    background: linear-gradient(30deg, #0D47A1, #2196F3);
    color: #fafafa!important; }

.button.button-grapefruit {
    background: linear-gradient(30deg, #D81B60, #F06292)!important; }

.header .header-menu .header-menu-one .badge {
    background: linear-gradient(30deg, #D81B60, #F06292)!important; }

input[type=checkbox].checker:checked + div.checker, input[type=checkbox].checker:indeterminate + div.checker {
    /*background-color: #D81B60;*/
    background: linear-gradient(30deg, #D81B60, #F06292)!important; }

.friends .friends-list .friends-list-one .friends-list-one-info .friends-list-one-info-nick span[mnpl-type=online] {
    background: linear-gradient(30deg, #D81B60, #F06292)!important; }
}
